INSERT INTO TB_BADDED146D (CVLCPT_ADT_YR, ADT_NO, ADT_STP_SECD, CVLCPT_DOC_ID, APRVR_JBPS_SECD, INCDNT_SQNO, DSPS_RQT_SQNO, TRPR_SQNO, TRPR_NM, TRPR_BRDT, TRPR_JBGD_SECD, TRPR_INST_CD, TRPR_DEPT_NM, FRST_USER_ID, LAST_USER_ID, LAST_PRCS_DEPT_CD, LAST_MENU_ID, FRST_REG_DT, LAST_MDFCN_DT) ( SELECT AA.ADT_YR /* 감사년도 */,
    AA.ADT_NO /* 감사번호 */,
    DECODE(#APRVR_JBPS_SECD#, '0010600', 'A1070', AA.ADT_STP_SECD) AS ADT_STP_SECD /* 감사단계구분코드 */,
    AA.CVLCPT_DOC_ID /* 문서아이디 */,
    #APRVR_JBPS_SECD# AS APRVR_JBPS_SECD /* 결재자직위구분코드 */,
    AA.INCDNT_SQNO /* 사건순번 */,
    AA.DSPS_RQT_SQNO /* 처분요구순번 */,
    (SELECT NVL(MAX(ST.TRPR_SQNO),0)+1 AS TRPR_SQNO /* 대상자순번 */
FROM TB_BADDED146D ST WHERE ST.CVLCPT_ADT_YR = AA.CVLCPT_ADT_YR AND ST.ADT_NO = AA.ADT_NO AND ST.ADT_STP_SECD = AA.ADT_STP_SECD AND ST.CVLCPT_DOC_ID = AA.CVLCPT_DOC_ID AND ST.APRVR_JBPS_SECD = #APRVR_JBPS_SECD# AND ST.INCDNT_SQNO = AA.INCDNT_SQNO AND ST.DSPS_RQT_SQNO = AA.DSPS_RQT_SQNO) AS TRPR_SQNO , AA.TRPR_NM , AA.TRPR_BRDT , AA.TRPR_JBGD_SECD , AA.TRPR_INST_CD , AA.TRPR_DEPT_NM , (SELECT USER_ID /* 사용자아이디 */
FROM TBDCMACM001M WHERE TRGT_NOP_ENO = BB.APRVR_ENO) , (SELECT USER_ID /* 사용자아이디 */
FROM TBDCMACM001M WHERE TRGT_NOP_ENO = BB.APRVR_ENO) , (SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TBDCMACM001M WHERE TRGT_NOP_ENO = BB.APRVR_ENO) , #FNL_MNU_ID# , SYSDATE , SYSDATE FROM TB_BADDED146D AA , ( SELECT A.CVLCPT_DOC_ID /* 민원문서아이디 */,
    B.APRVR_ENO /* 결재자직원전산번호 */,
    CASE WHEN B.APRVR_JBPS_SECD IN ('0058100','0035100') THEN '0015800' WHEN B.APRVR_JBPS_SECD = ('0045900') THEN '0055600' WHEN B.APRVR_JBPS_SECD IN ('0057400','0035400') THEN '0013600' ELSE B.APRVR_JBPS_SECD END AS APRVR_JBPS_SECD /* 결재자직위구분코드 */
FROM TBBADDED103M A , TBDCMACM023L B WHERE B.ATRZ_DOC_NO = #ATRZ_DOC_NO# AND A.ATRZ_DMND_ID = B.ATRZ_DOC_NO AND B.APV_SNO = ( SELECT MAX(D.APV_SNO)
FROM TBBADDED103M C , TBDCMACM023L D WHERE D.ATRZ_DOC_NO = B.ATRZ_DOC_NO AND C.ATRZ_DMND_ID = D.ATRZ_DOC_NO AND D.APV_SNO <> 1 AND C.SLCT_RPTP_KDCD = #SLCT_RPTP_KDCD# AND C.CVLCPT_DOC_ID = #CVLCPT_DOC_ID# AND D.APVR_STA_CD IN ('202','203','204') AND D.APRVR_JBPS_SECD IN ('0013600','0035400','0015800','0058100','0035100','0045900','0055600','0055700','0057400') ) AND A.SLCT_RPTP_KDCD = #SLCT_RPTP_KDCD# AND A.CVLCPT_DOC_ID = #CVLCPT_DOC_ID# AND B.APVR_STA_CD IN ('202','203','204') AND ROWNUM = 1 ) BB , TB_BADDED145M CC WHERE AA.CVLCPT_DOC_ID = #CVLCPT_DOC_ID# AND AA.CVLCPT_DOC_ID = BB.CVLCPT_DOC_ID AND CASE WHEN AA.APRVR_JBPS_SECD IN ('0058100','0035100') THEN '0015800' WHEN AA.APRVR_JBPS_SECD = ('0045900') THEN '0055600' ELSE AA.APRVR_JBPS_SECD END = BB.APRVR_JBPS_SECD AND AA.CVLCPT_ADT_YR = CC.ADT_YR AND AA.ADT_NO = CC.ADT_NO AND AA.CVLCPT_DOC_ID = CC.DOC_ID AND AA.APRVR_JBPS_SECD = CC.APRVR_JBPS_SECD AND AA.INCDNT_SQNO = CC.INCDNT_SQNO AND NOT EXISTS ( SELECT 1
FROM TB_BADDED146D ZZ WHERE ZZ.CVLCPT_ADT_YR = AA.CVLCPT_ADT_YR AND ZZ.ADT_NO = AA.ADT_NO AND ZZ.ADT_STP_SECD = AA.ADT_STP_SECD AND ZZ.CVLCPT_DOC_ID = AA.CVLCPT_DOC_ID AND ZZ.APRVR_JBPS_SECD = #APRVR_JBPS_SECD# AND ZZ.INCDNT_SQNO = AA.INCDNT_SQNO AND ZZ.DSPS_RQT_SQNO = AA.DSPS_RQT_SQNO AND ZZ.TRPR_NM = AA.TRPR_NM AND ZZ.TRPR_BRDT = AA.TRPR_BRDT ) AND NOT EXISTS ( SELECT 1
FROM TB_BADDED147M ZZ INNER JOIN TB_BADDED148M YY ON ZZ.CVLCPT_ADT_YR = YY.CVLCPT_ADT_YR AND ZZ.ADT_NO = YY.ADT_NO AND ZZ.ADT_STP_SECD = YY.ADT_STP_SECD AND ZZ.CVLCPT_DOC_ID = YY.CVLCPT_DOC_ID AND ZZ.APRVR_JBPS_SECD = YY.APRVR_JBPS_SECD AND ZZ.INCDNT_SQNO = YY.INCDNT_SQNO AND ZZ.DSPS_RQT_SQNO = YY.DSPS_RQT_SQNO AND ZZ.MPNG_SQNO = YY.MPNG_SQNO WHERE ZZ.CVLCPT_ADT_YR = AA.CVLCPT_ADT_YR AND ZZ.ADT_NO = AA.ADT_NO AND ZZ.CVLCPT_DOC_ID = AA.CVLCPT_DOC_ID AND ZZ.BFR_INCDNT_NO = F_MNG_KND_AUDYRNO(AA.CVLCPT_ADT_YR, AA.ADT_NO, CC.ADT_KDCD, '-') || '-' || CC.INCDNT_KDCD || '-' || (SELECT ST.INCDNT_DSPS_TPCD /* 사건처분유형코드 */
FROM TB_BADDED146M ST WHERE ST.CVLCPT_ADT_YR = AA.CVLCPT_ADT_YR AND ST.ADT_NO = AA.ADT_NO AND ST.ADT_STP_SECD = AA.ADT_STP_SECD AND ST.CVLCPT_DOC_ID = AA.CVLCPT_DOC_ID AND ST.APRVR_JBPS_SECD = AA.APRVR_JBPS_SECD AND ST.INCDNT_SQNO = AA.INCDNT_SQNO AND ST.DSPS_RQT_SQNO = AA.DSPS_RQT_SQNO) AND CASE WHEN ZZ.APRVR_JBPS_SECD IN ('0058100','0035100') THEN '0015800' WHEN ZZ.APRVR_JBPS_SECD = ('0045900') THEN '0055600' ELSE ZZ.APRVR_JBPS_SECD END = CASE WHEN #APRVR_JBPS_SECD# IN ('0058100','0035100') THEN '0015800' WHEN #APRVR_JBPS_SECD# = ('0045900') THEN '0055600' ELSE #APRVR_JBPS_SECD# END AND YY.INCDNT_MPNG_SECD IN ( 'D90', 'D70', 'D80', 'D99' ) ) )