UPDATE ( SELECT T1.CMPTN_YN /* 완료여부 */,
    T2.CMPTN_YN /* 완료여부 */,
    T2.LAST_USER_ID AS LAST_USER_ID /* 최종사용자아이디 */,
    T2.LAST_PRCS_DEPT_CD AS LAST_PRCS_DEPT_CD /* 최종처리부서코드 */,
    T2.LAST_MENU_ID AS LAST_MENU_ID /* 최종메뉴아이디 */,
    T2.LAST_MDFCN_DT AS LAST_MDFCN_DT /* 최종수정일시 */
FROM TBBADDED145M T1 INNER JOIN TBBADDED146M T2 ON (T1.ADT_YR = T2.CVLCPT_ADT_YR AND T1.ADT_NO = T2.ADT_NO AND T1.ADT_STP_SECD = T2.ADT_STP_SECD AND T1.DOC_ID = T2.CVLCPT_DOC_ID AND T1.APRVR_JBPS_SECD = T2.APRVR_JBPS_SECD AND T1.INCDNT_SQNO = T2.INCDNT_SQNO) WHERE T1.ADT_YR = #CVLCPT_ADT_YR# AND T1.ADT_NO = #ADT_NO# AND T1.APRVR_JBPS_SECD IN ('0010600', 'A1070') ) SET DSP_FNL_YN = AFF_FNL_YN ,  LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */,  LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */,  LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */,  LAST_MDFCN_DT = SYSDATE /* 최종수정일시 */