SELECT F_DPT_INFO(A.MNG_DEPT_CD, '1') AS F_DPT_INFO,
    F_MNG_KND_AUDYRNO(A.ADT_YR, A.ADT_NO, A.ADT_KDCD, '-') AS ADT_CLSF_NO /* 감사분류번호 */,
    A.ADT_MTTR_NM /* 감사사항명 */,
    C.MFEX_NO /* 소명자료 관리번호 */,
    C.IMMUN_NO /* 적극행정면책관리번호 */,
    (SELECT DISTINCT 'Y'
FROM TB_BADDED103M T1, TBDCMACM023L T2 WHERE T1.ATRZ_DMND_ID = T2.ATRZ_DOC_NO AND T1.CVLCPT_ADT_YR = A.ADT_YR AND T1.ADT_NO = A.ADT_NO AND T1.ADT_STP_SECD = 'A1060' AND T1.SLCT_RPTP_KDCD = 'A10600200' AND T2.APRVR_JBPS_SECD = '0055700' AND T2.APVR_STA_CD IN ('202','203') ) AS APV_YN /* 개별처리안 사무총장 결재 여부 */ , (SELECT T1.CNSTN_CMT_MTG_END_YN /* 자문위원회회의종료여부 */
FROM TB_CSPKDE109M T1 WHERE T1.CNSTN_CMT_MTG_YR = (SELECT MAX(CNSTN_CMT_MTG_YR) AS CNSTN_CMT_MTG_YR /* 자문위원회회의년도 */
FROM TB_CSPKDE103M WHERE CVLCPT_ADT_YR = C.CVLCPT_ADT_YR AND ADT_NO = C.ADT_NO AND FRS_MDM_SQNO = C.FRS_MDM_SQNO) AND T1.CNSTN_CMT_MTG_SQNO = (SELECT MAX(CNSTN_CMT_MTG_SQNO) AS CNSTN_CMT_MTG_SQNO /* 자문위원회회의순번 */
FROM TB_CSPKDE103M WHERE CVLCPT_ADT_YR = C.CVLCPT_ADT_YR AND ADT_NO = C.ADT_NO AND FRS_MDM_SQNO = C.FRS_MDM_SQNO) ) AS ADV_YN /*자문위원회 종료 여부*/ , (SELECT NVL(AGGR_CONCAT(ST.USR_DFN_TXT_1,''), '000')
FROM TBDCMACM013C ST WHERE ST.CMM_CD_DVSN_CD = '1000860' AND ST.CD = '20') AS RCPT_USR_CNB , (SELECT COUNT(ST.USR_DFN_TXT_1)
FROM TBDCMACM013C ST WHERE ST.CMM_CD_DVSN_CD = '1000860' AND ST.CD = '20') AS RCPT_USR_CNT FROM TB_BADDED001M A , TB_BADDED103M B , TBCSPKDE103M_VIEW C WHERE A.ADT_YR = B.ADT_YR AND A.ADT_NO = B.ADT_NO AND B.ADT_YR = C.CVLCPT_ADT_YR AND B.ADT_NO = C.ADT_NO AND B.RFRNC_YMD = (C.FRS_MDM_SQNO || C.EDOC_SECD) AND A.ADT_YR = #CVLCPT_ADT_YR# AND A.ADT_NO = #ADT_NO# AND B.DOC_ID = #CVLCPT_DOC_ID# AND B.RPTP_KDCD = #SLCT_RPTP_KDCD#