UPDATE TB_AEPMAR001M SET ATRZ_STTS_SECD = DECODE(#APV_STA_CD# ,'20',DECODE((SELECT COUNT(*)
FROM TBDCMACM023L WHERE ATRZ_DOC_NO = #ATRZ_DMND_ID# AND APRVR_KDCD <> '1' AND APVR_SEQ <> 1 AND APV_DH IS NOT NULL AND SUBSTR(APVR_STA_CD, 0,1) = '2'),0,'20','25') ,#APV_STA_CD#) /* 결재상태구분코드 */,  PRGRS_STTS_SECD = DECODE(#APV_STA_CD#,'30','50','40') /* 진행상태구분코드 */,  LAST_USER_ID = #LAST_USER_ID# /* 최종사용자ID */ ,  LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종거래부서코드 */ ,  LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴ID */ ,  LAST_MDFCN_DT = SYSDATE /* 최종수정일시 */ WHERE ATRZ_DMND_ID = #ATRZ_DMND_ID#