DECLARE BEGIN FOR CS IN ( SELECT A.SRT_VAL_NM,
    B.SRT_VAL_NM,
    A.CRU_SORT_SQNO /* 부패정렬순번 */,
    B.CRU_SORT_SQNO /* 부패정렬순번 */
FROM TBAEPGKM003L A, TBAEPGKM003L B WHERE A.TRGT_NOP_ENO = B.TRGT_NOP_ENO AND A.HIRK_CTG_ID = B.HIRK_CTG_ID AND A.TRGT_NOP_ENO = #TRGT_NOP_ENO# AND A.CTG_ID = #CHANGE_CTG_ID# AND B.CTG_ID = #CTG_ID# ) LOOP UPDATE TBAEPGKM003L SET SRT_VAL_NM = CS.PREV_NM,  CRU_SORT_SQNO = CS.PREV_NO /* 부패정렬순번 */,  LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */,  LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */,  LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */,  LAST_MDFCN_DT = SYSDATE /* 최종수정일시 */WHERE CTG_ID = #CTG_ID# AND TRGT_NOP_ENO = #TRGT_NOP_ENO# ; UPDATE TBAEPGKM003L SET SRT_VAL_NM = CS.CURR_NM,  CRU_SORT_SQNO = CS.CURR_NO /* 부패정렬순번 */,  LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */,  LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */,  LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */,  LAST_MDFCN_DT = SYSDATE /* 최종수정일시 */WHERE CTG_ID = #CHANGE_CTG_ID# AND TRGT_NOP_ENO = #TRGT_NOP_ENO# ; END LOOP ; END ;