DECLARE BEGIN FOR CS IN ( SELECT A.SRT_VAL_NM,
    B.SRT_VAL_NM
FROM TBAEPGKM017M A, TBAEPGKM017M B WHERE 1=1 AND A.TG_CAT_ID = #CHANGE_CAT_ID# AND B.TG_CAT_ID = #ORIGIN_CAT_ID# ) LOOP UPDATE TBAEPGKM017M SET SRT_VAL_NM = CS.CHANGE_NM,  LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */,  LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */,  LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */,  LAST_MDFCN_DT = SYSDATE /* 최종수정일시 */WHERE TG_CAT_ID = #ORIGIN_CAT_ID# ; UPDATE TBAEPGKM017M SET SRT_VAL_NM = CS.ORIGIN_NM,  LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */,  LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */,  LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */,  LAST_MDFCN_DT = SYSDATE /* 최종수정일시 */WHERE TG_CAT_ID = #CHANGE_CAT_ID# ; END LOOP ; END ;