SELECT T1.FMDA_IDX,
    TO_CHAR(T1.FMDA_AUDIT_ST, 'YYYY-MM-DD') || ' ~ ' ||TO_CHAR(T1.FMDA_AUDIT_ET, 'YYYY-MM-DD') AS TO_CHAR /* 감사기간 */,
    (SELECT AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM tbdcmacm002m WHERE USE_YN = 'Y' AND SRECS_DEPT_CD = T1.FMDA_DEPT ) AS FMDA_DEPT_NM , F_AWU_CODE_NM('10000002', T1.FMDA_PROC) AS FMDA_PROC_NM , T1.FMDA_SJ , TO_CHAR(T1.FMDA_PUBLIC_DT, 'YYYY-MM-DD') AS FMDA_PUBLIC_DT , T1.FMDA_PUBLIC_YN , (CASE WHEN T1.FMDA_PUBLIC_YN IS NULL THEN '공개전' WHEN T1.FMDA_PUBLIC_YN = 'Y' AND TO_CHAR(SYSDATE,'YYYYMMDD') = TO_CHAR(T1.FMDA_PUBLIC_DT,'YYYYMMDD' ) AND TO_CHAR(SYSDATE,'YYYYMMDD') NOT BETWEEN TO_CHAR(T1.FMDA_PUBLIC_DT,'YYYYMMDD' ) AND TO_CHAR(T1.FMDA_PUBLIC_DT+14,'YYYYMMDD' ) THEN '공개기간 경과' END) AS FMDA_HOME_ST FROM TBCSPTSM001M T1 WHERE USE_AT = 'Y' AND T1.FMDA_DEPT = #{fmdaDept} AND T1.FMDA_PROC = #{fmdaProcCode} AND T1.FMDA_SJ LIKE '%'||#{fmdaSearch}||'%' AND TO_CHAR(T1.FMDA_AUDIT_ST, 'YYYY') = #{fmdaYear} AND T1.FMDA_DEPT IN ( SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TBDCMACM002M WHERE USE_YN = 'Y' AND (HIRK_DPT_CD = #{fmdaHirkDept} OR DPT_CD = #{fmdaHirkDept}) ) ORDER BY FMDA_AUDIT_ST DESC, FMDA_IDX DESC