/* AWU.BAK.BRR.AWUBAKANT003EMapper.TBAWUBAKBRR002MsearchList1 */ SELECT T1.NTT_SJ AS RSCH_ASMT_TTL,
    T1.CVLCPT_DOC_ID /* 민원문서아이디 */,
    T1.NTT_ID AS FRS_MDM_SQNO,
    TO_CHAR(T1.FRST_REGIST_PNTTM, 'YYYY-MM-DD') AS FRST_REG_DT /* 최초등록일시 */,
    T1.BIZ_KEY1 || '-' || DECODE(T1.BIZ_KEY2, '1', '심사', '재심') ||'-' ||T1.BIZ_KEY3 AS ADT_CLSF_NO /* 감사분류번호 */
FROM TBCSPTSM002L T1 WHERE 1=1 AND T1.BBS_ID = 'BBSMSTR_100000000013' AND T1.BIZ_KEY2 = '1' AND T1.BIZ_KEY1 = #{srchAuditYear} AND T1.BIZ_KEY3 = #{srchAuditNum} AND T1.NTT_SJ LIKE '%'||#{srchAuditTitle}||'%' ORDER BY T1.BIZ_KEY1 DESC, LPAD(T1.BIZ_KEY3,5,'0') DESC