SELECT A.CD,
    (SELECT B.CD_NM
FROM TBDCMACM013C B WHERE B.CMM_CD_DVSN_CD = '1000739' AND B.CD = A.CD AND B.USE_YN = 'Y') AS CD_NM , A.OTPT_SEQ , SUBSTR(A.CD, 3, 5) AS EMPTY_CD FROM TBDCMACM013C A WHERE 1=1 AND A.CMM_CD_DVSN_CD = '1000007' AND A.USE_YN = 'Y' AND SUBSTR(A.CD,1,2) LIKE SUBSTR(#{srchAuditYear},3,2)||'%' ORDER BY A.OTPT_SEQ