UPDATE TBBADCMR003M SET INFO_YR = #INFO_YR# /* 정보년도 */,  LAG_CAT_SNO =#LAG_CAT_SNO# ,  MID_CAT_SNO=#MID_CAT_SNO# ,  SALL_CAT_SNO =#SALL_CAT_SNO# ,  SALL_CAT_TIT_NM=#SALL_CAT_TIT_NM# ,  CMPTN_YN = #CMPTN_YN# /* 완료여부 */,  MDFCN_YMD = TO_CHAR(SYSDATE,'YYYYMMDD') /* 수정일자 */,  MDFCN_PIC_ID = #MDFCN_PIC_ID# /* 수정담당자아이디 */,  LAST_MDFCN_DT = SYSDATE /* 최종수정일시 */,  LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */,  LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */,  LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */,  CPR_CHGR_ID = #TRGT_NOP_ENO# ,  CPR_DPT_CD = (SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */
