SELECT F_CODE_NM('1000256', ADT_MNTR_PRCS_STTS_SECD) AS F_CODE_NM /* 감사모니터링처리상태 */,
    CASE WHEN JRSD_MAO_SECD IS NOT NULL THEN (SELECT AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TBDCMACM015M WHERE INST_CD = D1.JRSD_MAO_SECD) || '(' || JRSD_MAO_SECD || ')' ELSE '' END AS COPT_GD_NM /*소관기관명*/ , F_CODE_NM('1000253', DATA_CLSF_SECD) AS MTRL_CAT_NM /*자료분류코드*/ , D2.EMPHS_MNG_STT_NM AS REL_EPS_MNG_LAW_NM /*중접법령*/ , CASE WHEN STT_KDCD IS NOT NULL THEN '(' || F_CODE_NM('1000254', STT_KDCD) ||') ' || TRIM(MNTR_NM) ELSE TRIM(MNTR_NM) END AS MNTR_NM /*모니터링 제목*/ , NVL2(MNTR_URL_ADDR, '', TRIM(MNTR_CN)) AS MNTR_CN /*모니터링 내용 - 모니터링 URL이 있을경우엔 내용을 보여주지 않는다.*/ , TRIM(STT_URL_ADDR) AS STT_URL_ADDR /*법령URL주소*/ , TRIM(#N/A) AS #N/A , GMD_PRCS_OPNN_CN , DECODE(NVL(PRCS_OPNN_YN, 'N'), 'Y', 'O', 'X') AS PRCS_OPNN_YN /*의견*/ , (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TB_BADCMR012M WHERE STT_YR = D1.STT_YR AND STT_MNTR_SQNO = D1.STT_MNTR_SQNO AND ADT_PLAN_DOC_TPCD = 'AD-MR-001') AS #N/A /*모니터링내용 첨부파일*/ FROM TB_BADCMR011M D1 , TB_BADCMR014M D2 WHERE D1.REL_EMPHS_MNG_STT_YR = D2.EMPHS_STT_YR (+) AND D1.REL_EMPHS_MNG_STT_SQNO = D2.EMPHS_STT_SQNO (+) AND ATRZ_RFRNC_ID = #ATRZ_RFRNC_ID# AND STT_MNTR_SQNO = #STT_MNTR_SQNO# AND STT_YR = #STT_YR#