SELECT STT_YR /* 법령년도 */,
    STT_MNTR_SQNO /* 법령모니터링순번 */,
    DATA_OCRN_YMD AS DATA_OCRN_YMD /* 자료발생일자 */,
    F_CODE_NM('1000253', DATA_CLSF_SECD) AS F_CODE_NM /* 자료구분 */,
    F_DPT_INFO(TKCG_DEPT_CD ,'1') AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    (SELECT AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TBDCMACM015M WHERE INST_CD = TBBADCMR011M.JRSD_MAO_SECD) AS COPT_GD_NM /*소관기관*/ , MNTR_NM AS MNTR_NM /*제목*/ , F_CODE_NM('1000256', ADT_MNTR_PRCS_STTS_SECD) AS AUD_MNT_HNDL_STA_NM /*처리상태*/ , NVL2(TRIM(REL_EMPHS_MNG_STT_YR), 'O', 'X') AS REL_EMPHS_MNG_STT_YR /*중점*/ , DECODE(NVL(PRCS_OPNN_YN, 'N'), 'Y', 'O', 'X') AS PRCS_OPNN_YN /*의견*/ , NVL2(TRIM(PRCS_OPNN_RPLY_YMD), 'O', '') AS HNDL_OPIN_RPL_YN /*회신*/ , '' AS EMPHS_STT_YR , '' AS EMPHS_STT_SQNO FROM TB_BADCMR011M WHERE 1 = 1 AND REL_EMPHS_MNG_STT_YR = #REL_EMPHS_MNG_STT_YR# AND REL_EMPHS_MNG_STT_SQNO = #REL_EMPHS_MNG_STT_SQNO# AND ADT_MNTR_PRCS_STTS_SECD IS NOT NULL ORDER BY STT_YR DESC, STT_MNTR_SQNO DESC