SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */,
    CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    SUBSTR(SRECS_DEPT_CD, 1, 3) AS SUBSTR
FROM TBDCMACM002M WHERE 1=1 AND SRECS_DEPT_CD NOT LIKE '___000' AND SRECS_DEPT_CD NOT IN (SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TBDCMACM002M WHERE 1=1 AND ( SRECS_DEPT_CD LIKE '___100' OR SRECS_DEPT_CD LIKE '___A00' OR SRECS_DEPT_CD IN ('040200','081600','170300','150120','200010','300120')) /*사복1,국민1-1,심사1,디지털감사,교육운영,연구지원*/ AND SRECS_DEPT_CD NOT IN ('200100', '400100')/*교육지원, 연습1 제외*/ AND USE_YN = 'Y') AND SUBSTR(SRECS_DEPT_CD, 1, 3) < '400' AND USE_YN ='Y' ORDER BY DPT_SNO,CRU_SORT_SQNO