SELECT STT_YR /* 법령년도 */,
    STT_MNTR_SQNO /* 법령모니터링순번 */,
    '(' || STT_YR || '-' || STT_MNTR_SQNO || ')' AS STT_YR /* 법령년도 */,
    ADT_MNTR_PRCS_STTS_SECD AS ADT_MNTR_PRCS_STTS_SECD /* 감사모니터링처리상태구분코드 */,
    (SELECT AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TBDCMACM015M WHERE INST_CD = TBBADCMR011M.JRSD_MAO_SECD) AS COPT_GD_NM /*소관기관명*/ , JRSD_MAO_SECD AS JRSD_MAO_SECD /*소관기관 코드*/ , DATA_CLSF_SECD AS DATA_CLSF_SECD /*자료분류코드*/ , CASE WHEN TRIM(REL_EMPHS_MNG_STT_YR) IS NULL OR TRIM(REL_EMPHS_MNG_STT_SQNO) IS NULL THEN '' ELSE REL_EMPHS_MNG_STT_YR ||'-'|| REL_EMPHS_MNG_STT_SQNO END AS REL_EPS_MNG_LAW /*중접법령*/ , STT_KDCD AS STT_KDCD /*법령종류코드*/ , TRIM(MNTR_NM) AS MNTR_NM /*모니터링 제목*/ , NVL2(MNTR_URL_ADDR, '', TRIM(MNTR_CN)) AS MNTR_CN /*모니터링 내용 - 모니터링 URL이 있을경우엔 내용을 보여주지 않는다.*/ , NVL2(MNTR_URL_ADDR, 'Y', 'N') AS MNT_URL_YN /*입법예고내용보기 버튼 보이고 말고*/ , TRIM(MNTR_URL_ADDR) AS MNTR_URL_ADDR /*모니터링 URL*/ , TRIM(STT_URL_ADDR) AS STT_URL_ADDR /*법령URL주소*/ , TRIM(CVLCPT_STT_LNKG_ID) AS CVLCPT_STT_LNKG_ID /*법령연결ID*/ , TRIM(#N/A) AS #N/A , TRIM(#N/A) AS #N/A , (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TB_BADCMR012M WHERE STT_YR = #STT_YR# AND STT_MNTR_SQNO = #STT_MNTR_SQNO# AND ADT_PLAN_DOC_TPCD = 'AD-MR-001') AS #N/A /*모니터링내용 첨부파일*/ , (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TB_BADCMR012M WHERE STT_YR = #STT_YR# AND STT_MNTR_SQNO = #STT_MNTR_SQNO# AND ADT_PLAN_DOC_TPCD = 'AD-MR-002') AS #N/A /*총괄과 처리의견 첨부파일*/ , (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TB_BADCMR012M WHERE STT_YR = #STT_YR# AND STT_MNTR_SQNO = #STT_MNTR_SQNO# AND ADT_PLAN_DOC_TPCD = 'AD-MR-003') AS DOC_ID_3 /*소관과 처리의견 첨부파일*/ , (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TB_BADCMR012M WHERE STT_YR = #STT_YR# AND STT_MNTR_SQNO = #STT_MNTR_SQNO# AND ADT_PLAN_DOC_TPCD = 'AD-MR-004') AS DOC_ID_4 /*국무회의첨부파일*/ , (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TB_BADCMR012M WHERE STT_YR = #STT_YR# AND STT_MNTR_SQNO = #STT_MNTR_SQNO# AND ADT_PLAN_DOC_TPCD = 'AD-MR-005') AS DOC_ID_5 /*총괄과 사후처리내용 첨부파일*/ , (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TB_BADCMR012M WHERE STT_YR = #STT_YR# AND STT_MNTR_SQNO = #STT_MNTR_SQNO# AND ADT_PLAN_DOC_TPCD = 'AD-MR-006') AS DOC_ID_6 /*소관과 사후처리내용 첨부파일*/ , GMD_DEPT_CD AS GMD_DEPT_CD /*주무과*/ , TRIM(GMD_PIC_ENO) AS GMD_PIC_ENO /*총괄담당자전산번호*/ , TRIM(GMD_PRCS_OPNN_CN) AS GMD_PRCS_OPNN_CN /*총괄과처리의견내용*/ , TKCG_DEPT_CD AS TKCG_DEPT_CD /*소관과*/ , TRIM(CPD_PIC_ENO) AS CPD_PIC_ENO /*담당과전산번호*/ , TRIM(CPD_PRCS_OPNN_CN) AS CPD_PRCS_OPNN_CN /*소관과처리의견내용*/ , PRCS_OPNN_YN AS PRCS_OPNN_YN /*의견유무*/ , TRIM(PRCS_OPNN_RPLY_YMD) AS PRCS_OPNN_RPLY_YMD /*회신일자*/ , TRIM(PRCS_OPNN_RFLT_YN_SECD) AS PRCS_OPNN_RFLT_YN_SECD /*변경여부*/ , TRIM(CBT_MTG_RCPT_YMD) AS CBT_MTG_RCPT_YMD /*국무회의상정일*/ , TRIM(CBT_MTG_CN) AS CBT_MTG_CN /*국무회의상정내용*/ , TRIM(GMD_EPFC_PRCS_CN) AS GMD_EPFC_PRCS_CN /*주무과처리의견*/ , TRIM(CPD_EPFC_PRCS_CN) AS CPD_EPFC_PRCS_CN /*소관과처리의견*/ , EPFC_PRCS_PRCS_OPNN_YN_YN AS EPFC_PRCS_PRCS_OPNN_YN_YN /*사후처리처리의견의견유무*/ , TRIM(EPFC_PRCS_OPNN_RPLY_YMD) AS EPFC_PRCS_OPNN_RPLY_YMD /*사후처리처리의견회신일*/ , EPFC_PRCS_RFLT_YN_SECD AS EPFC_PRCS_RFLT_YN_SECD /*사후처리반영여부코드*/ , TRIM(JDAF_DEPT_DRCTN_CN) AS JDAF_DEPT_DRCTN_CN /*지시내용*/ , TRIM(GMD_RCPT_YMD) AS GMD_RCPT_YMD /*총괄과저접수일자*/ , TRIM(GMD_RECL_DMND_RSN_CN) AS GMD_RECL_DMND_RSN_CN /*총괄과재분류요청사유*/ , TRIM(OPNN_RJCT_RSN_CN) AS OPNN_RJCT_RSN_CN /*의견반려사유*/ FROM TB_BADCMR011M WHERE STT_YR = #STT_YR# AND STT_MNTR_SQNO = #STT_MNTR_SQNO#