SELECT D1.AUD_YEAR AS AUD_YEAR /* 감사년도 */,
    D1.AUD_SNO AS AUD_SNO /* 감사연번 */,
    D1.AUD_YEAR||'-'||D1.STRTG_GOAL_CD||'-'||D1.AUD_REP_ORG_CD||'-'||D1.AUD_TYPE_CD ||'-'||D1.EXEC_DEPT_CD||'-'||D1.AUD_METHOD_CD||'-'||D1.AUD_SNO AS AUD_YEAR,
    D1.AUD_YEAR ||'-'||D1.AUD_SNO AS AUD_YEAR /* 감사연번 */,
    (SELECT AS NM_KOR
FROM COM030TL WHERE CODE_DIV='AA07' AND CODE =D1.AUD_TYPE_CD ) AS AUD_TYPE_NM /*감사종류코드명*/ , D1.AUD_TYPE_CD AS AUD_TYPE_CD /*감사종류코드*/ , D1.AUD_ITEM_NM AS AUD_ITEM_NM /*감사사항명*/ , (SELECT NM_KOR
FROM COM030TL WHERE CODE_DIV='DEPT02' AND CODE =D1.EXEC_DEPT_CD ) AS EXEC_DEPT_NM /*주관국과명*/ , D1.EXEC_DEPT_CD AS EXEC_DEPT_CD /*주관국과코드*/ , D1.SDATE AS SDATE /*감사시작일*/ , D1.EDATE AS EDATE /*감사종료일*/ , D1.MGR_DEPT_CD AS MGR_DEPT_CD /*관리부서*/ , D1.ADT_SCL_SECD AS ADT_SCL_SECD /*감사규모코드*/ , D1.TASK_CATE_CD AS TASK_CATE_CD /*업무분류222*/ , (SELECT NM_KOR
FROM COM030TL WHERE CODE_DIV='CM01' AND CODE =D1.AUD_REP_ORG_CD ) AS AUD_REP_ORG_NM /*대표기관코드명*/ , D1.AUD_REP_ORG_CD AS AUD_REP_ORG_CD /*대표기관코드*/ , D1.ORG_TYPE_CD AS ORG_TYPE_CD /*기관구분코드*/ , D1.AUD_METHOD_CD AS AUD_METHOD_CD /*감사방법코드*/ , D1.STRTG_GOAL_CD AS STRTG_GOAL_CD /*감사과제코드*/ , D1.EMPHS_SJT_CD AS EMPHS_SJT_CD /*중점과제코드*/ , D1.AUD_LEADER_EMPLNUM AS AUD_LEADER_EMPLNUM /*감사반장전산번호*/ , D1.AUD_LEADER_DEPT_CD AS AUD_LEADER_DEPT_CD /*감사반장부서번호*/ , D1.AUD_LEADER_POS_CLASS AS AUD_LEADER_POS_CLASS /*감사반장직급코드*/ , D1.MANAGER_EMPLNUM AS MANAGER_EMPLNUM /*감사주관자전산번호*/ , D1.MANAGER_DEPT_CD AS MANAGER_DEPT_CD /*감사주관자부서코드*/ , D1.MANAGER_POS_CLASS_CD AS MANAGER_POS_CLASS_CD /*감사주관자직급코드*/ , D1.ASSIST_EMPLNUM AS ASSIST_EMPLNUM /*감사보조자전산번호*/ , D1.ASSIST_POS_CLASS_CD AS ASSIST_POS_CLASS_CD /*감사보조자직급코드*/ , D1.ASSIST_DEPT_CD AS ASSIST_DEPT_CD /*감사보조자부서코드*/ FROM TB_BADDED001M D1 WHERE 1=1 AND DEL_YN IS NULL AND D1.AUD_YEAR = #strAudYear# AND D1.AUD_SNO = #strAudSno# AND D1.AUD_ITEM_NM LIKE '%'||#strAudItemNm#||'%' AND D1.AUD_STEP = #strAudStep# AND D1.AUD_TYPE_CD = #strAudTypeCd# AND D1.EXEC_DEPT_CD = #strExecDeptCd# AND D1.AUD_FG = #strAudFg# ORDER BY D1.AUD_SNO