SELECT C.ADT_YR AS ADT_YR /* 감사년도 */,
    C.ADT_NO AS ADT_NO /* 감사번호 */,
    A.DSPS_RQT_SQNO AS DSPS_RQT_SQNO /* 처분요구순번 */,
    F_MNG_KND_AUDYRNO(A.ADT_YR, A.ADT_NO, '', '-') AS F_MNG_KND_AUDYRNO,
    (SELECT AS ADT_KDCD /* 감사종류코드 */
FROM TB_BADDED001M WHERE CVLCPT_ADT_YR = A.CVLCPT_ADT_YR AND ADT_NO = A.ADT_NO) AS ADT_KDCD ,C.ADT_STP_SECD AS ADT_STP_SECD ,C.DOC_ID AS CVLCPT_DOC_ID ,C.RFRNC_ID AS TPOF_RFRNC_ID ,C.RPTP_NM AS SLCT_RPTP_NM ,C.RPTP_KDCD AS SLCT_RPTP_KDCD ,F_OPEN_EDIT(C.LNKG_URL_ADDR) AS DOC_TYPE /* 문서구분 추가 - 2019.08.21 yyh */ FROM TB_BADEAR001M A INNER JOIN TB_BADDED146M B ON A.CVLCPT_ADT_YR = B.ADT_YR AND A.ADT_NO = B.ADT_NO AND B.APRVR_JBPS_SECD = 'A1070' AND A.ADT_DSPS_RQT_SQNO = B.DSPS_RQT_SQNO INNER JOIN TB_BADDED103M C ON A.CVLCPT_ADT_YR = C.ADT_YR AND A.ADT_NO = C.ADT_NO AND B.ADT_STP_SECD = C.ADT_STP_SECD AND C.LAST_ADT_RPTP_YN = 'Y' AND B.DOC_ID = C.RFRNC_ID AND C.RPTP_KDCD = 'A10600200' AND C.ATRZ_STTS_SECD = '30' WHERE A.CVLCPT_ADT_YR = #CVLCPT_ADT_YR# AND A.ADT_NO = #ADT_NO# AND A.DSPS_RQT_SQNO = #DSPS_RQT_SQNO#