SELECT (SELECT AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TBDCMACM015M WHERE INST_CD = (A.SLCT_JRSD_SG_SECD || '000000000')) AS OFFC_ORG_NM , A.INST_CD , SUBSTRING(A.INST_CD, 1, 3)|| '-' ||SUBSTRING(A.INST_CD, 4, 3) ||'-'|| SUBSTRING(A.INST_CD, 7, 3) ||'-'|| SUBSTRING(A.INST_CD, 10, 3) AS ORG_RES , PROF_RBPRSN_INST_NM FROM TBDCMACM015M A WHERE 1=1 AND A.INST_CD >= #strOrgCdSt# AND A.INST_CD AND NOT EXISTS (SELECT 'Y'
FROM TB_BADDED008L B WHERE 1 = 1 AND B.ADT_BGNG_YMD BETWEEN #strBztStYY#||#strBztStMM#||#strBztStDD# AND #strBztEndYY#||#strBztEndMM#||#strBztEndDD# /*인자값1*/ AND A.INST_CD = B.INST_CD) AND ORG_DGE_CD = 2 AND USE_DVSN_NO = 'Y' ORDER BY INST_CD