SELECT B.OGDP_DEPT_CD /* 소속부서코드 */,
    (SELECT AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBDCMACM002M WHERE SRECS_DEPT_CD = B.OGDP_DEPT_CD) AS PROF_RBPRSN_INST_NM , #strBztStYY# || '.' || #strBztStMM# AS FROM_DATE , #strBztEndYY# || '.' || #strBztEndMM# AS TO_DATE , B.OGDP_DEPT_CD , A.ADT_BGNG_YMD FROM TB_BADDED006L A , TB_BADDED005L B WHERE 1 = 1 AND SUBSTR(A.ADT_BGNG_YMD, 1, 6) >= #strBztStYY#||#strBztStMM# AND SUBSTR(A.ADT_BGNG_YMD, 1, 6) AND B.OGDP_DEPT_CD = #OGDP_DEPT_CD# AND B.EMP_SECD != '0' GROUP BY B.OGDP_DEPT_CD,A.ADT_BGNG_YMD