SELECT F_CODE_NM('1000008',AUD.ADT_ASMT_SECD) AS F_CODE_NM,
    /*감사방침*/ SUM(PSON.ADT_DCN) AS ADT_DCN /* 감사일수 */,
    /*총계_일수 */ SUM(PSON.MAN_DAY ) AS MAN_DAY,
    /*총계_연인원*/ SUM (TTO.INST_CONT ) AS INST_CONT /* 기관개수 */,
    /*총계_기관*/ COUNT( CASE WHEN AUD.INST_SECD='10' THEN AUD.ADT_NO ELSE NULL END) + COUNT( CASE WHEN AUD.INST_SECD IN ('21','22') THEN AUD.ADT_NO ELSE NULL END) + COUNT( CASE WHEN AUD.INST_SECD='30' THEN AUD.ADT_NO ELSE NULL END) + COUNT( CASE WHEN AUD.INST_SECD >'30' THEN AUD.ADT_NO ELSE NULL END) AS INST_SECD /* 기관구분코드 */,
    /*총계_사항 */ SUM( CASE WHEN AUD.INST_SECD='10' THEN PSON.ADT_DCN ELSE 0 END) AS INST_SECD /* 기관구분코드 */,
    /*국가기관_일수*/ SUM( CASE WHEN AUD.INST_SECD='10' THEN PSON.MAN_DAY ELSE 0 END) AS INST_SECD /* 기관구분코드 */,
    /*국가기관_연인원*/ SUM( CASE WHEN AUD.INST_SECD='10' THEN TTO.INST_CONT ELSE 0 END) AS INST_SECD /* 기관구분코드 */,
    /*국가기관_기관*/ COUNT( CASE WHEN AUD.INST_SECD='10' THEN AUD.ADT_NO ELSE NULL END) AS INST_SECD /* 기관구분코드 */,
    /*국가기관_사항*/ SUM( CASE WHEN AUD.INST_SECD IN ('21','22') THEN PSON.ADT_DCN ELSE 0 END) AS INST_SECD /* 기관구분코드 */,
    /*자치단체_일수*/ SUM( CASE WHEN AUD.INST_SECD IN ('21','22') THEN PSON.MAN_DAY ELSE 0 END) AS INST_SECD /* 기관구분코드 */,
    /*자치단체_연인원*/ SUM( CASE WHEN AUD.INST_SECD IN ('21','22') THEN TTO.INST_CONT ELSE 0 END) AS INST_SECD /* 기관구분코드 */,
    /*자치단체_기관*/ COUNT( CASE WHEN AUD.INST_SECD IN ('21','22') THEN AUD.ADT_NO ELSE NULL END) AS INST_SECD /* 기관구분코드 */,
    /*자치단체_사항*/ SUM( CASE WHEN AUD.INST_SECD='30' THEN PSON.ADT_DCN ELSE 0 END) AS INST_SECD /* 기관구분코드 */,
    /*투자기관_일수*/ SUM( CASE WHEN AUD.INST_SECD='30' THEN PSON.MAN_DAY ELSE 0 END) AS INST_SECD /* 기관구분코드 */,
    /*투자기관_연인원*/ SUM( CASE WHEN AUD.INST_SECD='30' THEN TTO.INST_CONT ELSE 0 END) AS INST_SECD /* 기관구분코드 */,
    /*투자기관_기관*/ COUNT( CASE WHEN AUD.INST_SECD='30' THEN AUD.ADT_NO ELSE NULL END) AS INST_SECD /* 기관구분코드 */,
    /*투자기관_사항*/ SUM( CASE WHEN AUD.INST_SECD >'30' THEN PSON.ADT_DCN ELSE 0 END) AS INST_SECD /* 기관구분코드 */,
    /*기타기관_일수*/ SUM( CASE WHEN AUD.INST_SECD >'30' THEN PSON.MAN_DAY ELSE 0 END) AS INST_SECD /* 기관구분코드 */,
    /*기타기관_연인원*/ SUM( CASE WHEN AUD.INST_SECD >'30' THEN TTO.INST_CONT ELSE 0 END) AS INST_SECD /* 기관구분코드 */,
    /*기타기관_기관*/ COUNT( CASE WHEN AUD.INST_SECD >'30' THEN AUD.ADT_NO ELSE NULL END) AS INST_SECD /* 기관구분코드 */
FROM TB_BADDED002M SITU, TB_BADDED001M AUD, (SELECT BZTRP_YR /* 출장년도 */,
    BZTRP_SQNO /* 출장순번 */,
    MIN(ADT_BGNG_YMD) AS ADT_BGNG_YMD /* 감사시작일자 */,
    MAX(ADT_END_YMD) AS ADT_END_YMD /* 감사종료일자 */,
    MAX(ADT_DCN) AS ADT_DCN /* 감사일수 */,
    SUM(ADT_DCN) AS ADT_DCN /* 감사일수 */
FROM (SELECT TMA.BZTRP_YR /* 출장년도 */,
    TMA.BZTRP_SQNO /* 출장순번 */,
    TMA.BZTRVR_ENO /* 출장자직원전산번호 */,
    MIN(PTD.ADT_BGNG_YMD) AS ADT_BGNG_YMD /* 감사시작일자 */,
    MAX(PTD.ADT_END_YMD) AS ADT_END_YMD /* 감사종료일자 */,
    SUM(PTD.ADT_DCN) AS ADT_DCN /* 감사일수 */
FROM TBBADDED005L TMA, TBBADDED006L PTD WHERE PTD.BZTRP_YR = TMA.BZTRP_YR AND PTD.BZTRP_SQNO = TMA.BZTRP_SQNO AND PTD.FT_SQNO = TMA.FT_SQNO AND PTD.BZTRVR_ALTMNT_SQNO = TMA.BZTRVR_ALTMNT_SQNO AND PTD.BZTRP_YR BETWEEN #strBztStYY# AND #strBztEndYY# /*화면 감사년도 */ AND SUBSTR(PTD.ADT_BGNG_YMD, 1, 6) BETWEEN #strBztStYY#||#strBztStMM# AND #strBztEndYY#||#strBztEndMM# /*화면 감사기간*/ GROUP BY TMA.BZTRP_YR, TMA.BZTRP_SQNO, TMA.BZTRVR_ENO) PTD1 GROUP BY BZTRP_YR, BZTRP_SQNO ) PSON, (SELECT BZTRP_YR /* 출장년도 */,
    BZTRP_SQNO /* 출장순번 */,
    COUNT(*) AS INST_CONT /* 기관개수 */
FROM (SELECT DISTINCT AS BZTRP_YR,
    BZTRP_SQNO /* 출장순번 */,
    FT_TRGT_INST_SQNO /* 출장조대상기관순번 */
FROM TBBADDED008L WHERE SUBSTR(ADT_BGNG_YMD, 1, 6) BETWEEN #strBztStYY#||#strBztStMM# AND #strBztEndYY#||#strBztEndMM# /*화면 감사기간*/ ) TTO0 GROUP BY BZTRP_YR, BZTRP_SQNO ) TTO WHERE AUD.ADT_YR BETWEEN #strBztStYY# AND #strBztEndYY# /*화면 감사년도*/ AND SITU.REL_ADT_YR = AUD.ADT_YR AND SITU.REL_ADT_NO = AUD.ADT_NO AND SITU.BZTRP_SECD ='1' AND PSON.BZTRP_YR = SITU.BZTRP_YR AND PSON.BZTRP_SQNO = SITU.BZTRP_SQNO AND TTO.BZTRP_YR = SITU.BZTRP_YR AND TTO.BZTRP_SQNO = SITU.BZTRP_SQNO AND SUBSTR(AUD.ADT_ASMT_SECD,-1) BETWEEN #strAudTaskCdSt# AND #strAudTaskCdEnd# /*화면 감사지침 1~9*/ GROUP BY AUD.ADT_YR, AUD.ADT_ASMT_SECD