SELECT F_CODE_NM('1000001',TTO.RGN_CD) AS F_CODE_NM,
    /*지역명*/ F_ORG_INFO(TTO.INST_CD,'1') AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */,
    /*대상기관*/ AUD.TASK_SECD AS TASK_SECD /* 업무구분코드 */,
    /*업무분류코드*/ F_CODE_NM('1000120',AUD.TASK_SECD) AS F_CODE_NM,
    /*업무분류명*/ SUBSTRB(AUD.TASK_SECD, 1, 2)||'-'||SUBSTRB(AUD.TASK_SECD, 3, 2)||'-'||SUBSTRB(AUD.TASK_SECD, 5, 2) ||CHR(10)||F_CODE_NM('1000120',AUD.TASK_SECD) AS SUBSTRB,
    /*감사사항*/ SUBSTRB(PT.ADT_BGNG_YMD, 5, 2)||'.'||SUBSTRB(PT.ADT_BGNG_YMD, 7, 2)||'~'||CHR(10)||' '|| SUBSTRB(PT.ADT_END_YMD, 5, 2)||'.'||SUBSTRB(PT.ADT_END_YMD, 7, 2)||'('||PT.ADT_DCN||')' AS SUBSTRB,
    /*감사(일간)*/ SUBSTRB(PT.BZTRP_BGNG_YMD, 5, 2)||'.'||SUBSTRB(PT.BZTRP_BGNG_YMD, 7, 2)||'~'||CHR(10)||' '|| SUBSTRB(PT.BZTRP_END_YMD, 5, 2)||'.'||SUBSTRB(PT.BZTRP_END_YMD, 7, 2)||'('||PT.BZTRP_DCN||')' AS SUBSTRB,
    /*출장(일간)*/ TMA.OGDP_DEPT_CD AS OGDP_DEPT_CD /* 소속부서코드 */,
    /*소속*/ F_CODE_NM('1000173',TMA.BZTRVR_JBGD_SECD) AS F_CODE_NM,
    /*직급명*/ TMA.BZTRVR_NM AS BZTRVR_NM /* 출장자명 */,
    /*출장자명(성명)*/ TMA.BZTRVR_ENO AS BZTRVR_ENO /* 출장자직원전산번호 */
FROM TB_BADDED006L PT, TB_BADDED005L TMA, TB_BADDED008L TTO, TB_BADDED002M SITU, TB_BADDED001M AUD WHERE AUD.ADT_YR = #CVLCPT_ADT_YR# /*화면 인자값 감사년도*/ AND AUD.ADT_NO = #ADT_NO# /*화면 인자값 감사순번*/ AND SITU.REL_ADT_YR = AUD.ADT_YR AND SITU.REL_ADT_NO = AUD.ADT_NO AND SITU.ADT_STP_SQNO = #CVLCPT_ADT_STP_SQNO# /*화면 인자값 감사단계 */ AND PT.BZTRP_YR = SITU.BZTRP_YR AND PT.BZTRP_SQNO = SITU.BZTRP_SQNO AND TMA.BZTRP_YR = PT.BZTRP_YR AND TMA.BZTRP_SQNO = PT.BZTRP_SQNO AND TMA.FT_SQNO = PT.FT_SQNO AND TMA.BZTRVR_ALTMNT_SQNO = PT.BZTRVR_ALTMNT_SQNO AND TTO.BZTRP_YR = PT.BZTRP_YR AND TTO.BZTRP_SQNO = PT.BZTRP_SQNO AND TTO.FT_SQNO = PT.FT_SQNO AND TTO.FT_TRGT_INST_SQNO = PT.FT_TRGT_INST_SQNO ORDER BY PROF_RBPRSN_INST_NM,LCL_NM,TSK_CAT_CD_NM