SELECT TER.BZTRVR_ENO /* 출장자직원전산번호 */
FROM TB_BADDED010L TER, TB_BADDED011L TE, (SELECT BZTRP_YR /* 출장년도 */,
    BZTRP_SQNO /* 출장순번 */,
    BZTRVR_ENO /* 출장자직원전산번호 */,
    MAX(OGDP_DEPT_CD) AS OGDP_DEPT_CD /* 소속부서코드 */
FROM TBBADDED005L WHERE BZTRP_YR = #BZTRP_YR# AND BZTRP_SQNO = #BZTRP_SQNO# GROUP BY BZTRP_YR, BZTRP_SQNO, BZTRVR_ENO) DBO_BLL_BLNC WHERE TER.BZTRP_YR = #BZTRP_YR# AND TER.BZTRP_SQNO = #BZTRP_SQNO# AND BL.BZTRP_YR(+) = TER.BZTRP_YR AND BL.BZTRP_SQNO(+) = TER.BZTRP_SQNO AND BL.BZTRVR_ENO(+) = TER.BZTRVR_ENO AND TE.BZTRP_YR = TER.BZTRP_YR AND TE.BZTRP_SQNO = TER.BZTRP_SQNO AND TE.BZTRVR_ENO = TER.BZTRVR_ENO GROUP BY TER.BZTRVR_ENO ORDER BY 1