SELECT TMA.BZTRP_YR /* 출장년도 */,
    TMA.BZTRP_SQNO /* 출장순번 */,
    TMA.FT_SQNO /* 출장조순번 */,
    TMA.BZTRVR_ENO /* 출장자직원전산번호 */,
    /*전산번호*/ F_DPT_INFO(TMA.OGDP_DEPT_CD,'4') AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    /*소속*/ F_CODE_NM('1000173',TMA.BZTRVR_JBGD_SECD) AS F_CODE_NM,
    /*직급*/ TMA.BZTRVR_NM AS BZTRVR_NM /* 출장자명 */,
    /*성명*/ TMA.BZTRVR_ALTMNT_SQNO AS BZTRVR_ALTMNT_SQNO /* 출장자배정순번 */,
    /*출장자배정순번*/ (SELECT SUM(A.RL_FARE_AMT ) AS RL_FARE_AMT /* 철도운임금액 */
FROM TB_BADDED009L A WHERE TMA.BZTRP_YR = A.BZTRP_YR AND TMA.BZTRP_SQNO = A.BZTRP_SQNO AND TMA.FT_SQNO = A.FT_SQNO AND TMA.BZTRVR_ALTMNT_SQNO = A.BZTRVR_ALTMNT_SQNO) AS SUM_RL_FR_EXS FROM TB_BADDED005L TMA WHERE TMA.BZTRP_YR = #BZTRP_YR# AND TMA.BZTRP_SQNO = #BZTRP_SQNO# AND TMA.FT_SQNO = #FT_SQNO# AND TMA.DPTRE_RGN_SECD = #DPTRE_RGN_SECD# AND TMA.EMP_SECD <> '9' /*차출자 조회하지 않도록 - 2019.05.13 chm*/ AND TO_CHAR(TMA.BZTRVR_ALTMNT_SQNO) = #PCT_ALC_SNO# /* TBBADDED009L에 PCT_ALC_SNO 컬럼이 추가되어 이 컬럼에 값이 있을경우 만 조건을 걸어준다. - 2019.05.14 chm */