SELECT F_MNG_KND_AUDYRNO(AUD.ADT_YR, AUD.ADT_NO, AUD.ADT_KDCD, '-') ||'('||SITU.ADT_STP_SQNO || ')' AS F_MNG_KND_AUDYRNO,
    /*감사연번호*/ F_CODE_NM('1000007',AUD.ADT_KDCD) AS F_CODE_NM,
    /*감사종류명*/ F_DPT_INFO(SITU.BZTRP_DEPT_CD,'1') AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    /*소속*/ F_CODE_NM('1000751',AUD.BZTRP_BGT_SECD) AS F_CODE_NM /* 예산구분명 추가 - 2016.06.22 yyh */
FROM TB_BADDED001M AUD, TB_BADDED002M SITU WHERE SITU.BZTRP_YR = #BZTRP_YR# AND SITU.BZTRP_SQNO = #BZTRP_SQNO# AND AUD.ADT_YR = SITU.REL_ADT_YR AND AUD.ADT_NO = SITU.REL_ADT_NO