SELECT SITU.REL_ADT_YR /* 관계감사년도 */,
    /*감사년도*/ SITU.REL_ADT_NO AS REL_ADT_NO /* 관계감사번호 */,
    /*감사순번*/ SITU.ADT_STP_SQNO AS ADT_STP_SQNO /* 감사단계순번 */,
    /*감사단계*/ MAX(CASE WHEN T1.ADT_YR >= '2016' THEN F_CODE_NM('1000739',T1.ADT_KDCD) ELSE '-' END) AS ADT_YR /* 감사년도 */,
    #BZTRVR_ENO# AS TRGT_NOP_ENO /* 대상인원직원전산번호 */
FROM TB_BADDED002M SITU, TB_BADDED011L TE, TB_BADDED001M T1 WHERE SITU.BZTRP_YR = TE.BZTRP_YR AND SITU.BZTRP_SQNO = TE.BZTRP_SQNO AND SITU.BZTRP_SECD = '1' AND SITU.REL_ADT_YR = T1.ADT_YR AND SITU.REL_ADT_NO = T1.ADT_NO AND TE.BZTRVR_ENO = #BZTRVR_ENO# AND TE.BZTRP_BGNG_YMD >= #strBztStrDt# AND TE.BZTRP_END_YMD <= #strBztEndDt# AND TE.CMPTN_YN = 'Y' GROUP BY SITU.REL_ADT_YR, SITU.REL_ADT_NO, SITU.ADT_STP_SQNO ORDER BY SITU.REL_ADT_YR, SITU.REL_ADT_NO, SITU.ADT_STP_SQNO