SELECT SUM(TE.BZTRP_DCN) AS BZTRP_DCN /* 출장일수 */,
    /*총 출장일수*/ SUM(TE.LDG_DCN) AS LDG_DCN /* 숙박일수 */,
    /*총 숙박일수*/ SUM(TE.LDG_CST) AS LDG_CST /* 숙박비용 */,
    /*총 숙박비*/ SUM(TE.BZTRP_TRVEXP_FDEXP) AS BZTRP_TRVEXP_FDEXP /* 출장여비식비 */,
    /*총 식비*/ SUM(TE.BTEXP_CRTR_AMT) AS BTEXP_CRTR_AMT /* 출장비기준금액 */,
    /*총 일비*/ SUM(TE.LDG_CST + TE.BZTRP_TRVEXP_FDEXP + TE.BTEXP_CRTR_AMT) AS LDG_CST /* 숙박비용 */,
    /* 총 숙박비 + 총 식비 + 총 일비 합계*/ #BZTRVR_ENO# AS TRGT_NOP_ENO /* 대상인원직원전산번호 */
FROM TB_BADDED002M SITU, TB_BADDED011L TE WHERE SITU.BZTRP_YR = TE.BZTRP_YR AND SITU.BZTRP_SQNO = TE.BZTRP_SQNO AND SITU.BZTRP_SECD = '1' AND TE.BZTRVR_ENO = #BZTRVR_ENO# AND TE.BZTRP_BGNG_YMD >= #strBztStrDt# AND TE.BZTRP_END_YMD <= #strBztEndDt# AND TE.CMPTN_YN = 'Y' AND SITU.REL_ADT_YR = #REL_ADT_YR# AND SITU.REL_ADT_NO = #REL_ADT_NO# AND SITU.ADT_STP_SQNO = #CVLCPT_ADT_STP_SQNO#