UPDATE TB_BADDED180B A SET CRTR_END_YMD = (SELECT TO_CHAR(TO_DATE(MAX(B.CRTR_BGNG_DT),'yyyyMMdd')-1,'yyyyMMdd') AS CRTR_BGNG_DT /* 기준시작일시 */
FROM TB_BADDED180B B WHERE B.BZTRP_SECD = #BZTRP_SECD#) ,  LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */,  LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */,  LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */,  LAST_MDFCN_DT = SYSDATE /* 최종수정일시 */WHERE A.BZTRP_SQNO = (SELECT MAX(B.BZTRP_SQNO) AS BZTRP_SQNO /* 출장순번 */
FROM TB_BADDED180B B WHERE B.BZTRP_SECD = #BZTRP_SECD# AND B.BZTRP_SQNO != (SELECT MAX(B.BZTRP_SQNO) AS BZTRP_SQNO /* 출장순번 */
FROM TB_BADDED180B B WHERE B.BZTRP_SECD = #BZTRP_SECD#))