UPDATE TB_BADDED201B A SET CRTR_END_YMD = (SELECT TO_CHAR(TO_DATE(MAX(B.CRTR_BGNG_YMD),'yyyyMMdd')-1,'yyyyMMdd') AS CRTR_BGNG_YMD /* 기준시작일자 */
FROM TB_BADDED201B B WHERE B.BZTRP_SECD = #BZTRP_SECD#) ,  LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */,  LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */,  LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */,  LAST_MDFCN_DT = SYSDATE /* 최종수정일시 */WHERE A.BTEXP_FE_SQNO = (SELECT MAX(B.FRS_MDM_SQNO) AS FRS_MDM_SQNO /* 포렌식매체순번 */
FROM TB_BADDED201B B WHERE B.BZTRP_SECD = #BZTRP_SECD# AND B.FRS_MDM_SQNO != (SELECT MAX(B.BTEXP_FE_SQNO) AS BTEXP_FE_SQNO /* 출장비연비순번 */
FROM TB_BADDED201B B WHERE B.BZTRP_SECD = #BZTRP_SECD#))