    A.PRCS_TERM_YMD AS PRCS_TERM_YMD /* 처리기한일자 */,
    A.ACADT_BGNG_YMD AS ACADT_BGNG_YMD /* 실지감사시작일자 */,
    A.ACADT_END_YMD AS ACADT_END_YMD /* 실지감사종료일자 */,
    A.ACADT_ENFRC_DCN AS ACADT_ENFRC_DCN /* 실지감사실시일수 */,
    TO_CHAR(TO_DATE(A.ACADT_BGNG_YMD),'YYYY-MM-DD')||'-'||TO_CHAR(TO_DATE(A.ACADT_END_YMD),'YYYY-MM-DD') ||'('||A.ACADT_ENFRC_DCN ||')' AS TO_CHAR,
    A.DAG_YMD AS DAG_YMD /* 귀청일자 */,
    A.RPT_TERM_YMD AS RPT_TERM_YMD /* 보고기한일자 */,
    A.LDRV_ENO AS LDRV_ENO /* 주심위원직원전산번호 */,
    F_USR_INFO(A.LDRV_ENO,'2') AS LDRV_NM /* 주심위원명 */,
    A.SBCN_YMD AS SBCN_YMD /* 부의일자 */,
    A.ACADT_PRCS_STTS_SECD AS ACADT_PRCS_STTS_SECD /* 실지감사처리상태구분코드 */,
    ACADT_PRLG_BGNG_YMD AS ACADT_PRLG_BGNG_YMD /* 실지감사연장시작일자 */,
    ACADT_PRLG_END_YMD AS ACADT_PRLG_END_YMD /* 실지감사연장종료일자 */,
    SUBSTR(B.CVLCPT_ADT_YR, 3, 2) || '-' || SUBSTR(B.ADT_ASMT_SECD,-1) ||'-'|| B.INST_SECD || '-' || SUBSTR(B.ADT_KDCD, 3, 1) ||B.SPRVSN_DEPT_CD || '-' || SUBSTR(B.ADT_MTHD_SECD,-1)||'-' || B.ADT_NO ||'-' ||(SELECT MAX(C.ADT_STP_SQNO) AS CVLCPT_ADT_YR /* 민원감사년도 */
FROM TBBADDED002M C WHERE B.CVLCPT_ADT_YR = C.REL_ADT_YR AND B.ADT_NO = C.REL_ADT_NO) AS DTL_AUD_NO /*세부연번호*/ , B.ADT_KDCD AS ADT_KDCD /*감사종류코드*/ , F_CODE_NM('1000007',B.ADT_KDCD) AS AUD_KND_NM /*감사종류명*/ , PRLG_PRD_APRV_YMD AS PRLG_PRD_APRV_YMD /*연장기간승인일*/ , SNTHS_DPP_YN AS SNTHS_DPP_YN /*종합처리안여부*/ , PRCS_PRST_DSCTN AS PRCS_PRST_DSCTN /*처리현황내역*/ , STUT_RMRK_CN AS STUT_RMRK_CN /*상황비고*/ , MDFCN_YN AS MDFCN_YN /*수정여부*/ , PTOUT_NOCS AS PTOUT_NOCS /*지적건수*/ ,CASE WHEN trim(A.ENFC_ATRZ_YMD) IS NOT NULL THEN '-' /*시행결재일자가 있으면 지연일수 없음*/ WHEN trim(A.LDRV_MBCMT_ATRZ_YMD) IS NOT NULL THEN to_char(to_date(sysdate)-to_date(A.ENFC_ATRZ_TERM_YMD)) WHEN trim(A.OW_DHD_ATRZ_YMD) IS NOT NULL THEN to_char(to_date(sysdate)-to_date(A.LDRV_MBCMT_ATRZ_TERM_YMD)) WHEN trim(A.JDAF_SRNG_OFER_ATRZ_YMD) IS NOT NULL THEN to_char(to_date(sysdate)-to_date(A.PSD_DHD_ATRZ_TERM_YMD)) WHEN trim(A.AB_ATRZ_YMD) IS NOT NULL THEN to_char(to_date(sysdate)-to_date(A.DLRM_ATRZ_TERM_YMD)) WHEN trim(A.DAG_RPT_YMD) IS NOT NULL THEN to_char(to_date(sysdate)-to_date(A.RB_PRCS_TERM_YMD)) WHEN trim(A.DAG_RPT_YMD) IS NULL THEN to_char(to_date(sysdate)-to_date(A.RPT_TERM_YMD)) ELSE '-' END delay_cnt /*지연일수*/ /*성과평가 지연일수 구하기 ,CASE WHEN A.ENF_APV_DT IS NOT NULL THEN TO_CHAR(TO_DATE(A.ENF_APV_DT) - TO_DATE(A.CHF_CMTR_APV_DT) - 0) 성과평가에서 시행의 기준일수는 0으로 셋팅 WHEN A.CHF_CMTR_APV_DT IS NOT NULL THEN TO_CHAR(TO_DATE(A.CHF_CMTR_APV_DT) - TO_DATE(A.OW_DHD_APV_DT) - 0) 성과평가에서 주심위원의 기준일수는 0으로 셋팅 WHEN A.OW_DHD_APV_DT IS NOT NULL THEN TO_CHAR(TO_DATE(A.OW_DHD_APV_DT) - TO_DATE(A.JA_JDG_SSEC_APV_DT) - F_DDLN_DCN(AUD.AUD_YR, AUD.AUD_NO,'08')) WHEN A.JA_JDG_SSEC_APV_DT IS NOT NULL THEN TO_CHAR(TO_DATE(A.JA_JDG_SSEC_APV_DT) - TO_DATE(A.MDT_DDG_APV_DT) - F_DDLN_DCN(AUD.AUD_YR, AUD.AUD_NO,'07')) WHEN A.MDT_DDG_APV_DT IS NOT NULL THEN TO_CHAR(TO_DATE(A.MDT_DDG_APV_DT) - TO_DATE(A.MDT_OFER_APV_DT) - F_DDLN_DCN(AUD.AUD_YR, AUD.AUD_NO,'06')) WHEN A.MDT_OFER_APV_DT IS NOT NULL THEN TO_CHAR(TO_DATE(A.MDT_OFER_APV_DT) - TO_DATE(A.AUD_BRDV_APV_DT) - F_DDLN_DCN(AUD.AUD_YR, AUD.AUD_NO,'05')) WHEN A.AUD_BRDV_APV_DT IS NOT NULL THEN TO_CHAR(TO_DATE(A.AUD_BRDV_APV_DT) - TO_DATE(A.RTN_RPT_DT) - F_DDLN_DCN(AUD.AUD_YR, AUD.AUD_NO,'04')) WHEN A.RTN_RPT_DT IS NOT NULL THEN TO_CHAR(TO_DATE(A.RTN_RPT_DT) - TO_DATE(A.FLD_AUD_END_DT) - F_DDLN_DCN(AUD.AUD_YR, AUD.AUD_NO,'03')) ELSE NULL END DELAY_CNT 현단계지연일수*/ , CASE WHEN (A.ACADT_PRCS_STTS_SECD = '1' AND TO_DATE(A.DAG_YMD) > TO_DATE(SYSDATE)) THEN '감사중' WHEN (A.ACADT_PRCS_STTS_SECD = '1' AND TRIM(A.PRCS_TERM_YMD) IS NULL) OR A.ACADT_PRCS_STTS_SECD = '3' THEN '처리중' WHEN A.ACADT_PRCS_STTS_SECD = '1'AND NVL(TO_DATE(A.PRCS_TERM_YMD),'99991231') AND A.CVLCPT_ADT_YR >= #strAudYrSt# AND A.CVLCPT_ADT_YR AND B.ADT_GRNT_NO >= #strAudGrnNoSt# AND B.ADT_GRNT_NO AND B.MNG_DEPT_CD = #MNG_DEPT_CD# AND B.ADT_MTTR_NM LIKE '%'||#strAudSbjNm#||'%' AND ( CASE WHEN (A.ACADT_PRCS_STTS_SECD = '1' AND TO_DATE(A.DAG_YMD) > TO_DATE(SYSDATE)) THEN '7' WHEN ((A.ACADT_PRCS_STTS_SECD = '1' AND TRIM(A.PRCS_TERM_YMD) IS NULL) OR A.ACADT_PRCS_STTS_SECD = '3') OR (A.ACADT_PRCS_STTS_SECD = '1'AND NVL(TO_DATE(A.PRCS_TERM_YMD),'99991231') /*처리기한 경과*/ AND (A.ACADT_PRCS_STTS_SECD = '1'AND NVL(TO_DATE(A.PRCS_TERM_YMD),'99991231') /* 감사연번종류코드 추가 - 2015.12.22 yyh 감사연번종류코드 FROM ~ TO 에서 한개로 변경 - 2016.05.30 yyh */ AND SUBSTR(B.ADT_KDCD,3,1) = #strAudYrNoKndCdSt# ) ORDER BY CVLCPT_ADT_YR DESC, ADT_NO