SELECT D1.TASK_SECD /* 업무구분코드 */,
    D1.TASK_SQNO /* 업무순번 */,
    D1.ADT_MTTR_NM /* 감사사항명 */,
    D1.#N/A AS #N/A /* 문서아이디1 */,
    D1.RLS_YN /* 공개여부 */,
    D1.ADT_SQNO /* 감사순번 */,
    F_MNG_KND_AUDYRNO(D2.ADT_YR, D2.ADT_NO, D2.ADT_KDCD, '-') AS F_MNG_KND_AUDYRNO,
    D1.RLS_CYCL /* 공개차수 */,
    DECODE(D1.RLS_CYCL,NULL,NULL,'홈페이지 공개문 '||D1.RLS_CYCL||'차') AS RLS_CYCL /* 공개차수 */,
    (SELECT TO_CHAR(TO_DATE(MAX(ENFC_YMD)),'YYYY-MM-DD') AS ENFC_YMD /* 시행일자 */
FROM TB_BADEAR002D S1 WHERE S1.AUD_YR = D2.AUD_YR AND S1.AUD_NO = D2.AUD_NO) AS ENF_DT ,TO_CHAR(TO_DATE(D1.PRL_SMT_DT),'YYYY-MM-DD') AS PRL_SMT_DT , CASE WHEN D1.PRL_SMT_DT IS NOT NULL THEN TO_DATE(D1.PRL_SMT_DT) - (SELECT TO_DATE(MAX(ENFC_YMD)) AS ENFC_YMD /* 시행일자 */
FROM TB_BADEAR002D S1 WHERE S1.AUD_YR = D2.AUD_YR AND S1.AUD_NO = D2.AUD_NO) ELSE TO_DATE(SYSDATE) - (SELECT TO_DATE(MAX(ENFC_YMD)) AS ENFC_YMD /* 시행일자 */
FROM TB_BADEAR002D S1 WHERE S1.AUD_YR = D2.AUD_YR AND S1.AUD_NO = D2.AUD_NO) END AS DAY_CNT /*시행일 이후 경과일수*/ ,TO_CHAR(TO_DATE(REGI_DT),'YYYY-MM-DD') AS REGI_DT ,D1.APV_DOC_NO ,NVL(D1.PRL_SMT_YN,'N') AS PRL_SMT_YN ,D1.PRSS_STA_CD ,F_CODE_NM('1000756',D1.PRSS_STA_CD) AS PRSS_STA_NM FROM TB_BADEAR028M D1 ,TB_BADDED001M D2 WHERE AUD_SLN = #AUD_YR#||#AUD_NO# AND D2.ADT_YR = SUBSTR(D1.ADT_SQNO, 0, 4) AND D2.ADT_NO = SUBSTR(D1.ADT_SQNO, 5, 3)