UPDATE TB_BADEAR002D D1 SET ( BGT_RTRC_AMT /*예산절감*/ , INCM_INCRS_AMT /*수입증대*/ , PPL_BRDN_RDT_AMT /*국민부담경감금액*/ , PRPT_INCRS_AMT /*재산증가*/ , EXPND_RATI_AMT /*지출합리화*/ , PNRT_PRSRV_AMT /*추징회수보전금액*/ , RMBR_AMT /*환급금액*/ , SPLP_AMT /*추급금액*/ , EPFC_EFCT_AMT /*사후적효과금액*/ ) = (SELECT SUM(DECODE(EXPT_EFCT_SECD,'1',EXPT_EFCT_AMT,'')) AS EXPT_EFCT_SECD /* 기대효과구분코드 */,
    SUM(DECODE(EXPT_EFCT_SECD,'2',EXPT_EFCT_AMT,'')) AS EXPT_EFCT_SECD /* 기대효과구분코드 */,
    SUM(DECODE(EXPT_EFCT_SECD,'3',EXPT_EFCT_AMT,'')) AS EXPT_EFCT_SECD /* 기대효과구분코드 */,
    SUM(DECODE(EXPT_EFCT_SECD,'4',EXPT_EFCT_AMT,'')) AS EXPT_EFCT_SECD /* 기대효과구분코드 */,
    SUM(DECODE(EXPT_EFCT_SECD,'5',EXPT_EFCT_AMT,'')) AS EXPT_EFCT_SECD /* 기대효과구분코드 */,
    SUM(PNRT_PRSRV_AMT) AS PNRT_PRSRV_AMT /* 추징회수보전금액 */,
    SUM(RMBR_AMT) AS RMBR_AMT /* 환급금액 */,
    SUM(SPLP_AMT) AS SPLP_AMT /* 추급금액 */,
    SUM(EPFC_EFCT_AMT) AS EPFC_EFCT_AMT /* 사후효과금액 */
FROM TB_BADEAR003M WHERE 1=1 AND CVLCPT_ADT_YR = D1.ADT_YR AND ADT_NO = D1.ADT_NO AND DSPS_RQT_SQNO = D1.DSPS_RQT_SQNO GROUP BY CVLCPT_ADT_YR , ADT_NO , DSPS_RQT_SQNO) WHERE 1=1 AND D1.ADT_YR = #CVLCPT_ADT_YR# AND D1.ADT_NO = #ADT_NO# AND D1.DSPS_RQT_SQNO = #DSPS_RQT_SQNO#