DECLARE BEGIN FOR CS IN ( SELECT CVLCPT_ADT_YR /* 민원감사년도 */,
    ADT_NO /* 감사번호 */,
    DSPS_RQT_SQNO /* 처분요구순번 */,
    SRECS_REL_INST_CD /* 심사재심의관계기관코드 */,
    RLPR_SQNO /* 관계자순번 */
FROM TB_BADEAR007M WHERE 1=1 AND CVLCPT_ADT_YR = #CVLCPT_ADT_YR# AND ADT_NO = #ADT_NO# AND DSPS_RQT_SQNO = #DSPS_RQT_SQNO# AND SRECS_REL_INST_CD = #SRECS_REL_INST_CD# AND RLPR_SQNO = #RLPR_SQNO# ) LOOP DELETE FROM TB_BADEAR007M WHERE 1=1 AND CVLCPT_ADT_YR = CS.CVLCPT_ADT_YR AND ADT_NO = CS.ADT_NO AND DSPS_RQT_SQNO = CS.DSPS_RQT_SQNO AND SRECS_REL_INST_CD = CS.SRECS_REL_INST_CD AND RLPR_SQNO = CS.RLPR_SQNO ; END LOOP ; END;