SELECT MAX(MASTER.ADT_YR) AS CVLCPT_ADT_YR /* 민원감사년도 */,
    MAX(MASTER.ADT_NO) AS ADT_NO /* 감사번호 */,
    MAX(MASTER.DSPS_RQT_SQNO) AS DSPS_RQT_SQNO /* 처분요구순번 */,
    MAX((SELECT CASE WHEN CVLCPT_ADT_YR >= '2016' THEN F_CODE_NM('1000739',SUBSTR(ADT_KDCD,3,1)) ELSE '-' AS CVLCPT_ADT_YR /* 민원감사년도 */
FROM TB_BADDED001M WHERE CVLCPT_ADT_YR = MASTER.CVLCPT_ADT_YR AND ADT_NO = MASTER.ADT_NO)) AS AUD_YR_NO_KND_NM FROM TB_BADEAR001M MASTER ,TB_BADDED001M T1 WHERE 1=1 AND MASTER.CVLCPT_ADT_YR = T1.ADT_YR AND MASTER.ADT_NO = T1.ADT_NO AND T1.ADT_YR = #CVLCPT_ADT_YR# /*인자값1*/ AND T1.ADT_GRNT_NO = #ADT_GRNT_NO# /*감사부여번호 조건 추가 - 2016.06.01 yyh*/ AND SUBSTR(T1.ADT_KDCD,3,1) = #strAudYrNoKndCd# AND MASTER.DSPS_RQT_SQNO BETWEEN #strDspRqSnoSt# AND #strDspRqSnoEnd# /*인자값2*/