DECLARE BEGIN FOR CS IN ( SELECT CVLCPT_ADT_YR /* 민원감사년도 */,
    ADT_NO /* 감사번호 */,
    DSPS_RQT_SQNO /* 처분요구순번 */,
    SRECS_REL_INST_CD /* 심사재심의관계기관코드 */,
    RLPR_SQNO /* 관계자순번 */
FROM TB_BADEAR007M WHERE 1=1 AND CVLCPT_ADT_YR = #CVLCPT_ADT_YR# AND ADT_NO = #ADT_NO# AND DSPS_RQT_SQNO = #DSPS_RQT_SQNO# ) LOOP INSERT INTO TB_BADEAR017H (LINK_NO, CVLCPT_ADT_YR, ADT_NO, DSPS_RQT_SQNO, SRECS_REL_INST_CD, RLPR_SQNO, LINK_STTS_SECD, USE_YN /* 사용여부 */, FRST_USER_ID, LAST_USER_ID, LAST_PRCS_DEPT_CD, LAST_MENU_ID, FRST_REG_DT, LAST_MDFCN_DT) VALUES ( (SELECT NVL(MAX(LINK_NO),0)+1 AS LINK_NO /* 연계번호 */
FROM TB_BADEAR017H), CS.CVLCPT_ADT_YR, CS.ADT_NO, CS.DSPS_RQT_SQNO, CS.SRECS_REL_INST_CD, CS.RLPR_SQNO, 'D', 'N', #FRT_USR_ID#, #FNL_USR_ID#, #FNL_DEAL_DPT_CD#, #FNL_MNU_ID#, SYSDATE, SYSDATE ); END LOOP ; END;