select DETAIL.ENFC_DEPT_CD||'-'||DETAIL.DOC_SNDNG_NO||'('||DETAIL.DSPS_RQT_IDNO||')' AS ENFC_DEPT_CD /* 시행부서코드 */,
    /*시행문번호 및 일련번호*/ DETAIL.ENFC_DEPT_CD AS ENFC_DEPT_CD /* 시행부서코드 */,
    MASTER.ADT_NO /* 감사번호 */,
    MASTER.DSPS_RQT_SQNO /* 처분요구순번 */,
    DETAIL.ENFC_YMD /* 시행일자 */,
    /*시행일자*/ F_ORG_INFO(SUBSTR(DETAIL.REL_INST_CD, 1, 3)||'000000000','1') AS F_ORG_INFO,
    /*소관청명*/ F_ORG_INFO(DETAIL.REL_INST_CD,'1') AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */,
    /*기관명 */ NVL(DETAIL.PNRT_PRSRV_AMT, 0) AS DSPS_RQT_AMT /* 처분요구금액 */,
    /*금액(상단)*/ NVL(DETAIL.RMBR_AMT, 0) + NVL(DETAIL.SPLP_AMT, 0) AS RMBR_AMT /* 환급금액 */,
    /*금액(하단)*/ MASTER.DSPS_RQT_NM AS DSPS_RQT_NM /* 처분요구명 */
from TB_BADEAR001M master, TB_BADEAR002D detail where 1=1 and MASTER.ADT_YR = DETAIL.ADT_YR and MASTER.ADT_NO = DETAIL.ADT_NO and MASTER.DSPS_RQT_SQNO = DETAIL.DSPS_RQT_SQNO and MASTER.DSPS_RQT_KDCD in ('310', '390', '391', '395', '396') and NVL(DETAIL.PNRT_PRSRV_AMT, 0) + NVL(DETAIL.RMBR_AMT, 0) + NVL(DETAIL.SPLP_AMT, 0) != 0 and DETAIL.ENFC_YMD between #strFromDt# and #strToDt# /*화면 기간 인자값*/ and DETAIL.CFMTN_STTS_SECD != '0' ORDER BY DSPS_RQT_AMT DESC , TOT_AM DESC