select DETAIL.CMO_INST_CD AS CMO_INST_CD /* 소관청기관코드 */,
    ORG1.PROF_RBPRSN_INST_NM /* 증명책임자기관명 */,
    ORG2.PROF_RBPRSN_INST_NM /* 증명책임자기관명 */,
    DETAIL.ENFC_DEPT_CD ||'-'|| DETAIL.DOC_SNDNG_NO ||'-'|| DETAIL.DSPS_RQT_SQNO AS ENFC_DEPT_CD /* 시행부서코드 */,
    DETAIL.ENFC_YMD AS ENFC_YMD /* 시행일자 */,
    ETCCD.CD_NM,
    MASTER.DSPS_RQT_NM AS DSPS_RQT_NM /* 처분요구명 */,
    DETAIL.PNRT_PRSRV_AMT AS PNRT_PRSRV_AMT /* 추징회수보전금액 */,
    DETAIL.NOP_CONT AS NOP_CONT /* 인원개수 */
from TB_BADEAR001M master ,TB_BADEAR002D detail ,(select RELORG.ADT_YR AS ADT_YR /* 감사년도 */,
    RELORG.ADT_NO AS ADT_NO /* 감사번호 */,
    RELORG.DSPS_RQT_SQNO AS DSPS_RQT_SQNO /* 처분요구순번 */,
    COUNT(*) AS CNT
from TBBADEAR006M relorg ,TBBADFRE009M prss where RELORG.CVLCPT_ADT_YR = PRSS.CVLCPT_ADT_YR and RELORG.ADT_NO = PRSS.ADT_NO and RELORG.DSPS_RQT_SQNO = PRSS.DSPS_RQT_SQNO and RELORG.SRECS_REL_INST_CD= PRSS.SRECS_REL_INST_CD group by RELORG.CVLCPT_ADT_YR ,RELORG.ADT_NO ,RELORG.DSPS_RQT_SQNO ) prsset ,TBDCMACM015M org1 ,TBDCMACM015M org2 ,TBDCMACM013C etccd where 1=1 and DETAIL.ENFC_YMD between #strFromDt# and #strToDt# /*화면의 기간*/ and DETAIL.CFMTN_STTS_SECD != '2' and prsset.CNT >= #strPressCnt# /*화면의 인자값 독촉회수 1이상이면 :1, 독촉회수 2이상이면:2*/ and MASTER.ADT_YR = DETAIL.ADT_YR and MASTER.ADT_NO = DETAIL.ADT_NO and MASTER.DSPS_RQT_SQNO = DETAIL.DSPS_RQT_SQNO and MASTER.ADT_YR = PRSSET.CVLCPT_ADT_YR and MASTER.ADT_NO = PRSSET.ADT_NO and MASTER.DSPS_RQT_SQNO = PRSSET.DSPS_RQT_SQNO and DETAIL.CMO_INST_CD = ORG1.INST_CD and DETAIL.REL_INST_CD = ORG2.INST_CD and MASTER.DSPS_RQT_KDCD = ETCCD.CD and ETCCD.CMM_CD_DVSN_CD ='1000002' MASTER.INST_SECD = #INST_SECD#||'%' /*화면의 기관분류코드*/ MASTER.INST_SECD NOT IN ('10','21','22','30')/*기타 선택시*/ order by DETAIL.CMO_INST_CD ,DETAIL.REL_INST_CD