SELECT PSON.RLPR_NM AS RLPR_NM /* 관계자명 */,
    SUBSTR(DETAIL.ENFC_YMD,1,6)||'-'||DETAIL.ENFC_DEPT_CD||'-'||DETAIL.DOC_SNDNG_NO||'-'||DETAIL.DSPS_RQT_IDNO AS SUBSTR,
    PSON.RRNO_SCRTY AS RRNO_SCRTY /* 주민등록번호보안 */
FROM TB_BADEAR001M MASTER, TB_BADEAR002D DETAIL, TB_BADEAR007M PSON WHERE MASTER.ADT_YR = DETAIL.ADT_YR AND MASTER.ADT_NO = DETAIL.ADT_NO AND MASTER.DSPS_RQT_SQNO = DETAIL.DSPS_RQT_SQNO AND MASTER.ADT_YR = PSON.ADT_YR AND MASTER.ADT_NO = PSON.ADT_NO AND MASTER.DSPS_RQT_SQNO = PSON.DSPS_RQT_SQNO AND DETAIL.ENFC_YMD BETWEEN #strStartFromDt# /*화면 시행기간 범위 FROM*/||'01' AND #strStartToDt# /*화면 시행기간 범위 TO*/||'31' AND MASTER.DSPS_RQT_KDCD = '210' ORDER BY PSON.RLPR_NM