SELECT F_MNG_KND_AUDYRNO(A.ADT_YR, A.ADT_NO, T1.ADT_KDCD, '-') ||'-'|| A.DSPS_RQT_SQNO AS F_MNG_KND_AUDYRNO,
    TO_CHAR(TO_DATE(B.ENFC_YMD),'YYYY.MM.DD') AS ENFC_YMD /* 시행일자 */,
    F_CODE_NM('1000002',A.DSPS_RQT_KDCD) AS DSPS_RQT_KND_CVN /* 처분요구종류코드값명 */,
    NVL(C.PNRT_PRSRV_AMT,0) AS PNRT_PRSRV_AMT /* 추징회수보전금액 */,
    NVL(C.IJS_AMT,0) AS IJS_AMT /* 부당금액 */,
    TO_CHAR(NVL(C.NOP_RT,0),'999,990.99') AS NOP_RT /* 인원비율 */,
    CASE WHEN C.CTB_RT = 100 THEN '단독' ELSE '공동' END AS CTB_RT /* 기여비율 */,
    F_ORG_INFO(B.REL_INST_CD,'1') AS REL_INST_NM /* 관계기관명 */,
    A.DSPS_RQT_NM AS DSPS_RQT_NM /* 처분요구명 */
from TB_BADEAR001M A ,TB_BADEAR002D B ,TB_BADEAR004M C ,TB_BADDED001M T1 WHERE 1=1 AND A.ADT_YR = B.ADT_YR AND A.ADT_NO = B.ADT_NO AND A.ADT_YR = T1.ADT_YR AND A.ADT_NO = T1.ADT_NO AND A.DSPS_RQT_SQNO = B.DSPS_RQT_SQNO AND A.ADT_YR = C.ADT_YR AND A.ADT_NO = C.ADT_NO AND A.DSPS_RQT_SQNO = C.DSPS_RQT_SQNO AND B.ENFC_YMD BETWEEN #strStartFromDt#||'01' AND #strStartToDt#||'31' AND B.CFMTN_STTS_SECD !='0' AND A.DSPS_RQT_KDCD <='730' AND C.EXTT_ID = #EXTT_ID# ORDER BY B.ENFC_YMD