SELECT D2.ADT_YR AS ADT_YR /* 감사년도 */,
    D2.ADT_NO AS ADT_NO /* 감사번호 */,
    D2.DSPS_RQT_SQNO AS DSPS_RQT_SQNO /* 처분요구순번 */,
    D4.ADT_MTTR_NM /* 감사사항명 */,
    F_MNG_KND_AUDYRNO(D4.ADT_YR, D4.ADT_NO, D4.ADT_KDCD, '-') AS ADT_CLSF_NO /* 감사분류번호 */,
    F_MNG_KND_AUDYRNO(D4.ADT_YR, D4.ADT_NO, D4.ADT_KDCD, '-') || '-' || D2.DSPS_RQT_SQNO AS ADT_DSPS_RQT_SQNO /* 감사처분요구순번 */,
    D1.DSPS_RQT_NM AS DSPS_RQT_NM /* 처분요구명 */,
    NVL(D3.RLS_TTL_CN,D1.DSPS_RQT_NM) AS RLS_TTL_CN /* 공개제목내용 */,
    D3.RPRS_INST_CD /* 대표기관코드 */,
    (SELECT F_ORG_INFO(MIN(SRECS_REL_INST_CD),'1') AS SRECS_REL_INST_CD /* 심사재심의관계기관코드 */
FROM TB_BADEAR006M T1 WHERE 1=1 AND T1.ADT_YR = D1.ADT_YR AND T1.ADT_NO = D1.ADT_NO AND T1.DSPS_RQT_SQNO = D1.DSPS_RQT_SQNO) AS REL_INST_NM /*처분기관*/ , (SELECT MIN(SRECS_REL_INST_CD) AS SRECS_REL_INST_CD /* 심사재심의관계기관코드 */
FROM TB_BADEAR006M T1 WHERE 1=1 AND T1.ADT_YR = D1.ADT_YR AND T1.ADT_NO = D1.ADT_NO AND T1.DSPS_RQT_SQNO = D1.DSPS_RQT_SQNO) AS SRECS_REL_INST_CD /*처분기관*/ , '' AS DSP_RQ_RSLT /*처분결과*/ , D1.DSPS_RQT_KDCD AS DSPS_RQT_KDCD /*처분요구종류코드*/ , D5.CD_NM AS DSPS_RQT_KND_CVN /*처분요구종류코드명*/ , CASE WHEN D2.CFMTN_STTS_SECD = '1' THEN '처분확정' WHEN D2.CFMTN_STTS_SECD = '2' THEN '집행확정' ELSE '미확정' END AS DTM_STA_NM /*처분확정여부*/ , NVL(D2.CFMTN_STTS_SECD,0) AS CFMTN_STTS_SECD , CASE WHEN D3.HMPG_RLS_YN IS NOT NULL THEN D3.HMPG_RLS_YN WHEN D2.HMPG_RLS_YN IN ('1','N') THEN 'N' WHEN D2.HMPG_RLS_YN IS NULL THEN 'N' ELSE 'Y' END AS RLS_YN /*공개여부*/ , D5.USR_DFN_TXT_1 AS DSP_RQ_KND_CD2 /*홈페이지 공개용 처분요구종류코드*/ , SUBSTR(D2.ENFC_YMD,1,4) AS ENFC_YR , CASE WHEN D2.HMPG_RLS_YN IN ('1','N') THEN '비공개' WHEN D2.HMPG_RLS_YN IS NULL THEN '비공개' ELSE '공개' END AS HMPG_RLS_YN , D3.RLS_CN , F_CODE_NM('1000120',D2.TASK_SECD) AS TSK_CAT_NM /*업무분류명*/ ,D3.DOC_ID ,CASE WHEN D3.DOC_ID IS NULL AND CSD_TRSF_SCS_YN IS NULL THEN '공개문 미생성' WHEN D3.DOC_ID IS NULL AND CSD_TRSF_SCS_YN = 'Z' THEN '공개문 미존재' WHEN D3.DOC_ID IS NOT NULL AND CSD_TRSF_SCS_YN IS NULL THEN '공개문 생성중' WHEN D3.DOC_ID IS NOT NULL AND CSD_TRSF_SCS_YN = 'Y' THEN '공개문 생성 성공' WHEN D3.DOC_ID IS NOT NULL AND CSD_TRSF_SCS_YN = 'N' THEN '공개문 생성 실패' ELSE '' END AS CSD_YN_NM ,D3.CSD_TRSF_SCS_YN AS CSD_TRSF_SCS_YN FROM TB_BADEAR001M D1 /*처분요구사항기본*/ ,TB_BADEAR002D D2 /*처분요구사항상세*/ ,TB_BADEAR030M D3 /*홍보제출기본*/ ,TB_BADDED001M D4 ,TBDCMACM013C D5 WHERE 1=1 AND D4.ADT_YR = D1.ADT_YR AND D4.ADT_NO = D1.ADT_NO AND D1.ADT_YR = D2.ADT_YR AND D1.ADT_NO = D2.ADT_NO AND D1.DSPS_RQT_SQNO = D2.DSPS_RQT_SQNO AND D1.ADT_YR = D3.ADT_YR(+) AND D1.ADT_NO = D3.ADT_NO(+) AND D1.DSPS_RQT_SQNO = D3.DSPS_RQT_SQNO(+) AND D1.ADT_YR = #CVLCPT_ADT_YR# AND D1.ADT_NO = #ADT_NO# AND D5.CMM_CD_DVSN_CD = '1000002' AND D5.CD <> '000000000' AND D1.DSPS_RQT_KDCD = D5.CD AND D1.DSPS_RQT_KDCD NOT IN ( '120' /* 무책판정 */ ,'390' /* 현지시정 */ ,'391' /* 현지시정회 */ ,'490' /* 현지주의 */ ,'395' /* 시정(국장전결) */ ,'396' /* 시정(국장전결)회 */ ,'495' /* 주의(국장전결) */ ,'730' /* 수사요청 */ ,'904' /* 종합통보 */ ,'905' /* 개별통보 */ ,'910' /* 자체처리요구 */ ,'911' /* 자체징계 */ ,'912' /* 자체시정 */ ,'913' /* 자체주의 */ ,'914' /* 자체통보 */ ,'915' /* 자체처리고발 */ ,'916' /* 불문 */ ) ORDER BY D1.DSPS_RQT_SQNO