UPDATE TB_BADEAR030M SET RLS_TTL_CN = #RLS_TTL_CN# /*공개제목내용*/ , SRECS_REL_INST_CD = #SRECS_REL_INST_CD# /* 심사재심의관계기관코드 */, REL_INST_NM = #REL_INST_NM# /* 관계기관명 */, LAST_USER_ID = #LAST_USER_ID# /*최종사용자ID*/ , LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /*최종거래부서코드*/ , LAST_MENU_ID = #LAST_MENU_ID# /*최종메뉴ID*/ , LAST_MDFCN_DT = SYSDATE /*최종수정일시*/ , HMPG_RLS_YN = #HMPG_RLS_YN# /* 누리집공개여부 */, RLS_CN = #RLS_CN# /* 공개내용 */WHERE CVLCPT_ADT_YR = #CVLCPT_ADT_YR# AND ADT_NO = #ADT_NO# AND DSPS_RQT_SQNO = #DSPS_RQT_SQNO#