SELECT T1.ADT_YR /* 감사년도 */,
    T1.ADT_NO /* 감사번호 */,
    T1.DSPS_RQT_SQNO /* 처분요구순번 */,
    T1.REL_INST_CD /* 관계기관코드 */,
    T1.RLPR_SQNO /* 관계자순번 */,
    T1.IMPL_SQNO /* 집행순번 */,
    ( SELECT MAX(ST1.IMPL_SQNO) + 1 AS IMPL_SQNO /* 집행순번 */
FROM TB_BADFRE008M ST1 WHERE 1 = 1 AND ST1.ADT_YR = T1.ADT_YR AND ST1.ADT_NO = T1.ADT_NO AND ST1.DSPS_RQT_SQNO = T1.DSPS_RQT_SQNO AND ST1.REL_INST_CD = T1.REL_INST_CD AND ST1.RLPR_SQNO = T1.RLPR_SQNO ) AS MAX_DFNT_ENFM_SNO ,T1.IMPL_TPCD ,F_CODE_NM('1000003', T1.IMPL_TPCD) AS ENFM_SHP_NM ,T1.PNL_AMT ,T1.IMPL_YMD ,T1.IMPL_DSCTN ,T1.IMPL_NOCS ,(SELECT RLPR_NM /* 관계자명 */
FROM TB_BADEAR007M WHERE CVLCPT_ADT_YR = T1.ADT_YR AND ADT_NO = T1.ADT_NO AND DSPS_RQT_SQNO = T1.DSPS_RQT_SQNO AND SRECS_REL_INST_CD = T1.REL_INST_CD AND RLPR_SQNO = T1.RLPR_SQNO AND ROWNUM = 1 ) AS RLPR_NM ,T1.FRST_USER_ID ,T1.LAST_USER_ID ,T1.LAST_PRCS_DEPT_CD ,T1.LAST_MENU_ID ,T1.FRST_REG_DT ,T1.LAST_MDFCN_DT FROM TB_BADFRE008M T1 WHERE 1 = 1 AND T1.ADT_YR = #CVLCPT_ADT_YR# AND T1.ADT_NO = #ADT_NO# AND T1.DSPS_RQT_SQNO = #DSPS_RQT_SQNO# AND T1.REL_INST_CD = #SRECS_REL_INST_CD# AND T1.RLPR_SQNO = #RLPR_SQNO# ORDER BY T1.ADT_YR, T1.ADT_NO, T1.DSPS_RQT_SQNO, T1.REL_INST_CD, T1.RLPR_SQNO, T1.IMPL_SQNO