SELECT T1.ADT_YR /* 감사년도 */,
    T1.ADT_NO /* 감사번호 */,
    T1.DSPS_RQT_SQNO /* 처분요구순번 */,
    T1.REL_INST_CD /* 관계기관코드 */,
    (SELECT AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TBDCMACM015M WHERE INST_CD = T1.REL_INST_CD AND ROWNUM = 1) AS REL_INST_NM, T1.RLPR_SQNO, T1.RLPR_ADDR, T1.OCPT_SECD, F_CODE_NM('1000057', T1.OCPT_SECD) AS OCPT_NM, T1.JBGD_SECD, F_CODE_NM('1000058', T1.JBGD_SECD) AS JBGD_NM, T1.RLPR_NM, DECODE(T1.RRNO_SCRTY,'','',SUBSTR(T1.RRNO_SCRTY,0,6)||'-'||SUBSTR(T1.RRNO_SCRTY,7,1)||'*') AS CRU_RRNO_SCRTY, T1.BFR_WRC_ADDR, T1.CO_NM, T1.ACNTG_CDNM, T1.RLPR_ACTN_SECD, T1.DSPS_RQT_AMT, DECODE(T1.CPLT_YN, 'Y', 'Y', 'N') AS CPLT_YN, DECODE(T1.CPLT_YN, 'Y', '완결', '미결') AS CPLT_YN_NM, T1.ACTN_RQT_KDCD, F_CODE_NM('1000011', T1.ACTN_RQT_KDCD) AS HND_RQ_KND_NM, T1.CPLT_YMD, T1.CMPT_ID, T1.SLDT_GRNTE_AMT, T1.SLDT_GRNTE_RSN_CN FROM TB_BADEAR007M T1 WHERE 1 = 1 AND T1.ADT_YR = #CVLCPT_ADT_YR# AND T1.ADT_NO = #ADT_NO# AND T1.DSPS_RQT_SQNO = #DSPS_RQT_SQNO# AND T1.REL_INST_CD = #SRECS_REL_INST_CD# ORDER BY T1.ADT_YR, T1.ADT_NO, T1.DSPS_RQT_SQNO, T1.REL_INST_CD, T1.RLPR_SQNO