SELECT T1.SRECS_RCPT_YR AS SRECS_RCPT_YR /* 심사재심의접수년도 */,
    T1.SRECS_RCPT_SECD AS SRECS_RCPT_SECD /* 심사재심의접수구분코드 */,
    T1.SRECS_RCPT_SQNO AS SRECS_RCPT_SQNO /* 심사재심의접수순번 */,
    T1.SRECS_DEPT_CD AS SRECS_DEPT_CD /* 심사재심의부서코드 */,
    F_DPT_INFO(T1.SRECS_DEPT_CD,'1') AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    T1.SRECS_RCPT_YMD AS SRECS_RCPT_YMD /* 심사재심의접수일자 */,
    T2.SRECS_DOC_ID /* 심사재심의문서아이디 */,
    DECODE(NVL(T2.SRECS_DOC_ID,''),'','','파일보기') AS ENAIS_ATCH_FILE_NM /* ENAIS첨부파일명 */,
    T1.SRECS_SRNG_CLM_SECD AS SRECS_SRNG_CLM_SECD /* 심사재심의심사청구구분코드 */,
    F_CODE_NM ('1000043', T1.SRECS_SRNG_CLM_SECD) AS SRECS_SRNG_CLM_SE_NM /* 심사재심의심사청구구분명 */,
    CASE WHEN T1.SRECS_RCPT_YR >= '2016' THEN T1.SRECS_RCPT_YR || '-' || DECODE(T1.SRECS_RCPT_SECD,'1','심사','2','재심','3','재심판정') || '-' || T1.SRECS_RCPT_SQNO ELSE T1.SRECS_RCPT_YR || '-' || T1.SRECS_RCPT_SECD || '-' || T1.SRECS_RCPT_SQNO END AS NTFCTN_RCPT_SQNO /* 통보접수순번 */,
    T1.SRECS_CLMT_NM AS SRECS_CLMT_NM /* 심사재심의청구인명 */,
    T1.SRECS_INST_CD AS SRECS_INST_CD /* 심사재심의기관코드 */,
    F_ORG_INFO(T1.SRECS_INST_CD, '1') AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */,
    T1.SRECS_CORP_SECD AS SRECS_CORP_SECD /* 심사재심의법인구분코드 */,
    F_CODE_NM ('1000436', T1.SRECS_CORP_SECD) AS F_CODE_NM /* 개인법인구분 */,
    T1.SRECS_AGT_NM AS SRECS_AGT_NM /* 심사재심의대리인명 */,
    T1.SRECS_ISSUE_CN AS SRECS_ISSUE_CN /* 심사재심의이슈내용 */,
    T2.SRECS_SBCN_NMTM AS SRECS_SBCN_NMTM /* 심사재심의부의횟수 */,
    T2.SRECS_SBCN_NO || '(' || T2.SRECS_SBCN_NMTM || '회)' AS SBCN_NO /* 부의번호 */,
    T1.SRECS_PRCS_TERM_YMD AS SRECS_PRCS_TERM_YMD /* 심사재심의처리기한일자 */,
    CASE WHEN (T3.SRECS_ENFC_YMD IS NULL AND T2.SRECS_SBCN_YMD IS NULL) THEN TO_DATE(SYSDATE) - TO_DATE(T1.SRECS_RCPT_YMD) || '일' ELSE CASE WHEN TRIM(T2.SRECS_SBCN_YMD) IS NOT NULL THEN TO_DATE(T2.SRECS_SBCN_YMD) - TO_DATE(T1.SRECS_RCPT_YMD) || '일' ELSE TO_DATE(T3.SRECS_ENFC_YMD) - TO_DATE(T1.SRECS_RCPT_YMD) || '일' END END AS GPCD_PRCS_YMD /* 처리일자 */,
    T2.SRECS_DVCF_ATRZ_YMD AS SRECS_DVCF_ATRZ_YMD /* 심사재심의과장결재일자 */,
    TO_DATE(TRIM(T2.SRECS_DVCF_ATRZ_YMD)) - TO_DATE(TRIM(T1.SRECS_RCPT_YMD)) || '일' AS TO_DATE /* 과장처리기간 */,
    T2.SRECS_DDG_ATRZ_YMD AS SRECS_DDG_ATRZ_YMD /* 심사재심의심의관결재일자 */,
    TO_DATE(TRIM(T2.SRECS_DDG_ATRZ_YMD)) - TO_DATE(TRIM(T1.SRECS_RCPT_YMD)) || '일' AS TO_DATE /* 심의관처리기간 */,
    T2.SRECS_BRUCHF_ATRZ_YMD AS SRECS_BRUCHF_ATRZ_YMD /* 심사재심의국장결재일자 */,
    TO_DATE(TRIM(T2.SRECS_BRUCHF_ATRZ_YMD)) - TO_DATE(TRIM(T1.SRECS_RCPT_YMD)) || '일' AS TO_DATE /* 국장처리기간 */,
    T2.SRECS_OW_PSD_ATRZ_YMD AS SRECS_OW_PSD_ATRZ_YMD /* 심사재심의사무총장결재일자 */,
    TO_DATE(TRIM(T2.SRECS_OW_PSD_ATRZ_YMD)) - TO_DATE(TRIM(T1.SRECS_RCPT_YMD)) || '일' AS TO_DATE /* 사무총장처리기간 */,
    T2.SRECS_GNLGR_ATRZ_YMD AS SRECS_GNLGR_ATRZ_YMD /* 심사재심의원장결재일자 */,
    TO_DATE(TRIM(T2.SRECS_GNLGR_ATRZ_YMD)) - TO_DATE(TRIM(T1.SRECS_RCPT_YMD)) || '일' AS TO_DATE /* 원장처리기간 */,
    T2.SRECS_MBCMT_ATRZ_YMD AS SRECS_MBCMT_ATRZ_YMD /* 심사재심의위원결재일자 */,
    TO_DATE(TRIM(T2.SRECS_MBCMT_ATRZ_YMD)) - TO_DATE(TRIM(T1.SRECS_RCPT_YMD)) || '일' AS TO_DATE /* 위원처리기간 */,
    T2.SRECS_SBCN_YMD AS SRECS_SBCN_YMD /* 심사재심의부의일자 */,
    TO_DATE(TRIM(T2.SRECS_SBCN_YMD)) - TO_DATE(TRIM(T1.SRECS_RCPT_YMD)) || '일' AS TO_DATE /* 부의처리기간 */,
    T3.SRECS_ENFC_YMD AS SRECS_ENFC_YMD /* 심사재심의시행일자 */,
    TO_DATE(TRIM(T3.SRECS_ENFC_YMD)) - TO_DATE(TRIM(T1.SRECS_RCPT_YMD)) || '일' AS TO_DATE /* 시행처리기간 */,
    T2.SRECS_CMT_LDRV_MBCMT_NM AS SRECS_CMT_LDRV_MBCMT_NM /* 심사재심의위원회주심위원위원명 */,
    T1.SRECS_PRCS_PIC_NM AS SRECS_PRCS_PIC_NM /* 심사재심의처리담당자명 */,
    T2.SRECS_DCS_KDCD AS SRECS_DCS_KDCD /* 심사재심의의결종류코드 */,
    F_CODE_NM('1000045', T2.SRECS_DCS_KDCD) AS SRECS_DCS_KND_CN /* 심사재심의의결종류내용 */,
    T2.SRECS_AMIT_AMT AS SRECS_AMIT_AMT /* 심사재심의용인금액 */,
    T3.SRECS_IMPL_YMD AS SRECS_IMPL_YMD /* 심사재심의집행일자 */,
    T3.SRECS_IMPL_CN AS SRECS_IMPL_CN /* 심사재심의집행내용 */,
    T3.SRECS_LWS_RAIS_YMD AS SRECS_LWS_RAIS_YMD /* 심사재심의소송제기일자 */,
    T3.SRECS_JDGM_YMD AS SRECS_JDGM_YMD /* 심사재심의판결일자 */,
    T3.SRECS_JDGM_CN AS SRECS_JDGM_CN /* 심사재심의판결내용 */,
    T3.SRECS_HLDOF_YMD AS SRECS_HLDOF_YMD /* 심사재심의보류일자 */,
    T3.SRECS_RMV_YMD AS SRECS_RMV_YMD /* 심사재심의해제일자 */,
    T3.SRECS_NYD_PRCS_RSN_CN AS SRECS_NYD_PRCS_RSN_CN /* 심사재심의미처분처리사유내용 */,
    T3.SRECS_PRCS_PLAN_CN AS SRECS_PRCS_PLAN_CN /* 심사재심의처리계획내용 */,
    T2.SRECS_DCSN_YR AS SRECS_DCSN_YR /* 심사재심의결정년도 */,
    T2.SRECS_DCSN_SECD AS SRECS_DCSN_SECD /* 심사재심의결정구분코드 */,
    T2.SRECS_DCSN_NO AS SRECS_DCSN_NO /* 심사재심의결정번호 */,
    CASE WHEN TRIM(T2.SRECS_DCSN_SECD) IS NULL THEN '' ELSE F_CODE_NM ('1000046', T2.SRECS_DCSN_SECD) || '-' || T2.SRECS_DCSN_YR || '-' || T2.SRECS_DCSN_NO END AS TRIM /* 결정사항번호 */,
    T2.SRECS_DCSN_MTTR_CN AS SRECS_DCSN_MTTR_CN /* 심사재심의결정사항내용 */,
    (SELECT AS DSPS_RQT_NM /* 처분요구명 */
FROM TB_BADEAR001M WHERE CVLCPT_ADT_YR = T1.CVLCPT_ADT_YR AND ADT_NO = T1.ADT_NO AND DSPS_RQT_SQNO = T1.DSPS_RQT_SQNO ) AS T2P_TIT_TXT /*관련 처분요구*/ , T1.RSCH_ASMT_TTL AS RSCH_ASMT_TTL /*제목명(청구내용)*/ , T2.SRECS_RMRK_CN AS RMRK_CN /*비고*/ , T1.CLM_YMD AS CLM_YMD /*청구일자*/ , T1.SRECS_RPT_YMD AS SRECS_RPT_YMD /*보고일자*/ , T1.SRECS_REL_AMT AS SRECS_REL_AMT /*관련금액*/ , T1.SRECS_RLPR_NOPE AS SRECS_RLPR_NOPE /*관계자수*/ , T1.CVLCPT_ADT_YR AS CVLCPT_ADT_YR /*감사년도*/ , T1.ADT_NO AS ADT_NO /*감사번호*/ , T1.DSPS_RQT_SQNO AS DSPS_RQT_SQNO /*처분요구순번*/ , T1.LAST_USER_ID AS LAST_USER_ID /*최종사용자ID*/ , T1.LAST_MDFCN_DT AS LAST_MDFCN_DT /*최종수정일자*/ , T2.SRECS_SRNG_CLM_PRCS_SECD AS SRECS_SRNG_CLM_PRCS_SECD /*심사심의처리구분코드*/ , T2.SRECS_DCSN_YMD AS SRECS_DCSN_YMD /*결정일*/ , T3.SRECS_OFDOC_NO AS SRECS_OFDOC_NO /*공문서번호*/ , NVL(T3.SRECS_LWS_YN,'N') AS SRECS_LWS_YN /* 소송유무 - 2015.05.26 yyh */ , DECODE(T3.SRECS_LWS_YN,'Y','유','무') AS LWS_YO_NM /* 소송유무명 - 2015.05.26 yyh */ , T3.SRECS_FRTRL_CT_NM AS SRECS_FRTRL_CT_NM /* 1심법원명 - 2015.05.26 yyh */ , T3.SRECS_FRTRL_INCDNT_NO_CN AS SRECS_FRTRL_INCDNT_NO_CN /* 1심사건번호 - 2015.05.26 yyh */ , T3.SRECS_LAST_RSLT_CN AS SRECS_LAST_RSLT_CN /* 종국결과 - 2015.05.26 yyh */ FROM TB_BADFRE012M T1 , TB_BADFRE012D T2 , TB_BADFRE013D T3 WHERE 1=1 AND T1.CVLCPT_RCPT_YR = T2.SRECS_RCPT_YR(+) AND T1.SRECS_RCPT_SECD = T2.SRECS_RCPT_SECD(+) AND T1.SRECS_RCPT_SQNO = T2.SRECS_RCPT_SQNO(+) AND T1.SRECS_DEPT_CD = T2.SRECS_DEPT_CD(+) AND T1.CVLCPT_RCPT_YR = T3.SRECS_RCPT_YR(+) AND T1.SRECS_RCPT_SECD = T3.SRECS_RCPT_SECD(+) AND T1.SRECS_RCPT_SQNO = T3.SRECS_RCPT_SQNO(+) AND T1.SRECS_DEPT_CD = T3.SRECS_DEPT_CD(+) AND T1.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND T1.SRECS_RCPT_SECD = #SRECS_RCPT_SECD# AND T1.SRECS_RCPT_SQNO = #SRECS_RCPT_SQNO# AND T1.SRECS_DEPT_CD = #SRECS_DEPT_CD# AND T1.SRECS_SRNG_CLM_SECD LIKE '1%' AND T1.SRECS_SRNG_CLM_SECD LIKE '2%' AND T1.SRECS_SRNG_CLM_SECD LIKE '3%' AND T1.SRECS_SRNG_CLM_SECD LIKE '4%' AND T1.SRECS_SRNG_CLM_SECD = #SRECS_SRNG_CLM_SECD# AND T1.SRECS_CLMT_NM LIKE '%' || #strReqr# || '%' AND T1.RSCH_ASMT_TTL LIKE '%' || #strTitNm# || '%' AND T1.INST_CD = #INST_CD# AND T2.SRECS_CMT_LDRV_MBCMT_NM LIKE '%' || #strCmtChfCmtrNm# || '%' AND T1.SRECS_PRCS_PIC_NM LIKE '%' || #strHndlChgr# || '%' AND T2.SRECS_DCSN_SECD = #SRECS_DCSN_SECD# AND T2.SRECS_DCSN_YR = #SRECS_DCSN_YR# AND T2.SRECS_DCSN_NO = #SRECS_DCSN_NO# AND T2.SRECS_DVCF_ATRZ_YMD IS NOT NULL AND T2.SRECS_BRUCHF_ATRZ_YMD IS NOT NULL AND T2.SRECS_OW_PSD_ATRZ_YMD IS NOT NULL AND T2.SRECS_GNLGR_ATRZ_YMD IS NOT NULL AND T2.SRECS_MBCMT_ATRZ_YMD IS NOT NULL AND T2.SRECS_SBCN_YMD IS NOT NULL AND T3.SRECS_ENFC_YMD IS NOT NULL /* 주석처리 - 2015.05.26 yyh AND T3.LWS_INST_DT IS NOT NULL */ AND NVL(T3.SRECS_LWS_YN,'N') = 'Y' AND T3.SRECS_HLDOF_YMD IS NOT NULL AND T3.SRECS_RMV_YMD IS NOT NULL AND T3.SRECS_IMPL_YMD IS NOT NULL AND T1.CVLCPT_ADT_YR IS NOT NULL AND ( T1.SRECS_AGT_NM LIKE '%' || #strSchTxt# || '%' OR T1.SRECS_PRCS_PIC_NM LIKE '%' || #strSchTxt# || '%' OR T1.RSCH_ASMT_TTL LIKE '%' || #strSchTxt# || '%' OR T2.SRECS_DCSN_MTTR_CN LIKE '%' || #strSchTxt# || '%' OR T3.SRECS_JDGM_CN LIKE '%' || #strSchTxt# || '%' OR T1.SRECS_ISSUE_CN LIKE '%' || #strSchTxt# || '%' OR T1.SRECS_CLMT_NM LIKE '%' || #strSchTxt# || '%' /* 추가 - 2015.05.26 yyh*/ OR T3.SRECS_FRTRL_CT_NM LIKE '%' || #strSchTxt# || '%' OR T3.SRECS_FRTRL_INCDNT_NO_CN LIKE '%' || #strSchTxt# || '%' OR T3.SRECS_LAST_RSLT_CN LIKE '%' || #strSchTxt# || '%' ) AND (T3.SRECS_ENFC_YMD IS NULL AND T2.SRECS_SBCN_YMD IS NULL) AND TO_DATE(TO_CHAR(SYSDATE, 'YYYYMMDD')) - TO_DATE(T1.NTFCTN_RCPT_YMD,'YYYYMMDD') >= 30 AND TO_DATE(TO_CHAR(SYSDATE, 'YYYYMMDD')) - TO_DATE(T1.NTFCTN_RCPT_YMD,'YYYYMMDD') >= 60 AND TO_DATE(TO_CHAR(SYSDATE, 'YYYYMMDD')) >= ADD_MONTHS(TO_DATE(T1.NTFCTN_RCPT_YMD),6) AND TO_DATE(TO_CHAR(SYSDATE, 'YYYYMMDD')) >= ADD_MONTHS(TO_DATE(T1.NTFCTN_RCPT_YMD),12) AND (T3.SRECS_ENFC_YMD IS NOT NULL OR T2.SRECS_SBCN_YMD IS NOT NULL) AND TO_DATE(NVL(T2.SRECS_SBCN_YMD,T3.SRECS_ENFC_YMD)) - TO_DATE(T1.NTFCTN_RCPT_YMD,'YYYYMMDD') >= 30 AND TO_DATE(NVL(T2.SRECS_SBCN_YMD,T3.SRECS_ENFC_YMD)) - TO_DATE(T1.NTFCTN_RCPT_YMD,'YYYYMMDD') >= 60 AND TO_DATE(NVL(T2.SRECS_SBCN_YMD,T3.SRECS_ENFC_YMD)) >= ADD_MONTHS(TO_DATE(T1.NTFCTN_RCPT_YMD),6) AND TO_DATE(NVL(T2.SRECS_SBCN_YMD,T3.SRECS_ENFC_YMD)) >= ADD_MONTHS(TO_DATE(T1.NTFCTN_RCPT_YMD),12) AND T1.NTFCTN_RCPT_YMD >= #strStartDt1# AND T1.NTFCTN_RCPT_YMD AND T3.SRECS_ENFC_YMD >= #strStartDt1# AND T3.SRECS_ENFC_YMD AND T3.SRECS_IMPL_YMD >= #strStartDt1# AND T3.SRECS_IMPL_YMD AND T3.SRECS_LWS_RAIS_YMD >= #strStartDt1# AND T3.SRECS_LWS_RAIS_YMD AND T3.SRECS_JDGM_YMD >= #strStartDt1# AND T3.SRECS_JDGM_YMD AND T1.NTFCTN_RCPT_YMD >= #strStartDt2# AND T1.NTFCTN_RCPT_YMD AND T3.SRECS_ENFC_YMD >= #strStartDt2# AND T3.SRECS_ENFC_YMD AND T3.SRECS_IMPL_YMD >= #strStartDt2# AND T3.SRECS_IMPL_YMD AND T3.SRECS_LWS_RAIS_YMD >= #strStartDt2# AND T3.SRECS_LWS_RAIS_YMD AND T3.SRECS_JDGM_YMD >= #strStartDt2# AND T3.SRECS_JDGM_YMD ORDER BY T1.CVLCPT_RCPT_YR DESC, T1.SRECS_RCPT_SQNO DESC