SELECT D1.SRECS_RCPT_YR /* 심사재심의접수년도 */,
    CASE WHEN F_CODE_NM('1000045', D2.SRECS_DCS_KDCD) LIKE '인용%' THEN '인용' ELSE F_CODE_NM('1000045', D2.SRECS_DCS_KDCD) END AS SRECS_DCS_KND_CN /* 심사재심의의결종류내용 */,
    CASE WHEN F_CODE_NM('1000045', D2.SRECS_DCS_KDCD) LIKE '인용%' THEN '('||REPLACE(F_CODE_NM('1000045', D2.SRECS_DCS_KDCD), '인용-', '')||')' ELSE '' END AS F_CODE_NM,
    D1.SRECS_RCPT_SQNO /* 심사재심의접수순번 */,
    REPLACE(D1.SRECS_CLMT_NM,'(주)','㈜') AS SRECS_CLMT_NM /* 심사재심의청구인명 */,
    NVL(D1.SRECS_DSPS_INST_NM, F_ORG_INFO(D1.SRECS_INST_CD, '1')) AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */,
    D1.SRECS_RCPT_YR||'-'||CASE WHEN D1.SRECS_SRNG_CLM_SECD IS NOT NULL AND D1.SRECS_SRNG_CLM_SECD IN ('40', '41', '42', '43', '49') THEN '재심' ELSE DECODE(SUBSTR(D1.SRECS_SRNG_CLM_SECD, 1, 1), '1', '국', '2', '지', '3', '기') END ||' '||D1.SRECS_SRNG_CLM_SE_SQNO AS SRECS_RCPT_YR /* 심사재심의접수년도 */,
    DECODE(D1.SRECS_RCPT_SECD,'1','심사','2','재심','3','재심판정') AS SRECS_RCPT_SECD /* 심사재심의접수구분코드 */
FROM TB_BADFRE012M D1 , TB_BADFRE012D D2 WHERE 1 = 1 AND D1.SRECS_RCPT_YR = D2.SRECS_RCPT_YR AND D1.SRECS_RCPT_SECD = D2.SRECS_RCPT_SECD AND D1.SRECS_RCPT_SQNO = D2.SRECS_RCPT_SQNO AND D1.SRECS_DEPT_CD = D2.SRECS_DEPT_CD D1.SRECS_RCPT_YR||D1.SRECS_RCPT_SECD||D1.SRECS_RCPT_SQNO||D1.SRECS_DEPT_CD = #tmpKey[]#