SELECT MASTER.CVLCPT_ADT_YR AS CVLCPT_ADT_YR /* 민원감사년도 */,
    MASTER.ADT_NO AS ADT_NO /* 감사번호 */,
    T1.ADT_GRNT_NO AS ADT_GRNT_NO /* 감사부여번호 */,
    MASTER.DSPS_RQT_SQNO AS DSPS_RQT_SQNO /* 처분요구순번 */,
    F_MNG_KND_AUDYRNO(MASTER.CVLCPT_ADT_YR, MASTER.ADT_NO, T1.ADT_KDCD, '-') || '-' || MASTER.DSPS_RQT_SQNO AS F_MNG_KND_AUDYRNO,
    CASE WHEN T1.ADT_YR >= '2016' THEN SUBSTR(T1.ADT_KDCD,3,1) ELSE '' END AS ADT_YR /* 감사년도 */,
    RELORG.REL_INST_CD AS REL_INST_CD /* 관계기관코드 */,
    (SELECT AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TBDCMACM015M WHERE INST_CD = RELORG.REL_INST_CD) AS AUD_ORG_CNT , MASTER.DSPS_RQT_KDCD AS DSPS_RQT_KDCD , (SELECT CD_NM
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD = '1000002' AND CD = MASTER.DSPS_RQT_KDCD) AS DSPS_RQT_KND_CVN , DETAIL.ENFC_DEPT_CD ENFC_DEPT_CD , (SELECT CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBDCMACM002M WHERE SRECS_DEPT_CD = DETAIL.ENFC_DEPT_CD) AS OPER_BURDIV_NM , DETAIL.IMPL_MNG_JRSD_DEPT_CD AS IMPL_MNG_JRSD_DEPT_CD , (SELECT CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBDCMACM002M WHERE SRECS_DEPT_CD = DETAIL.IMPL_MNG_JRSD_DEPT_CD) AS ENFM_MNG_BRDV_NM , DETAIL.PNRT_PRSRV_AMT AS PNRT_PRSRV_AMT , DETAIL.ENFC_YMD AS ENFC_YMD , RELPSN.RLPR_NM AS DETAILUSR_NAM , RELPSN.RLPR_ADDR AS OTSD_EXPRT_ADDR , RELPSN.DSPS_RQT_AMT AS DSPS_RQT_AMT , DETAIL.ENFC_YMD AS ENFC_YMD /*시행일*/ , DETAIL.ENFC_DEPT_CD AS ENFC_DEPT_CD /*시행국과코드*/ , F_DPT_INFO(DETAIL.ENFC_DEPT_CD,'1') AS ENF_BRDV_NM /*시행국과명*/ , MASTER.DSPS_RQT_NM AS DSPS_RQT_NM /*제목내용*/ , DETAIL.RVW_RPTP_DSPS_RQT_CN AS RVW_RPTP_DSPS_RQT_CN /*검토보고서처분요구요지*/ FROM TB_BADEAR001M MASTER , TB_BADEAR002D DETAIL , TB_BADEAR006M RELORG , TB_BADEAR007M RELPSN , TB_BADDED001M T1 WHERE 1=1 AND MASTER.CVLCPT_ADT_YR = DETAIL.ADT_YR AND MASTER.ADT_NO = DETAIL.ADT_NO AND MASTER.DSPS_RQT_SQNO = DETAIL.DSPS_RQT_SQNO AND MASTER.CVLCPT_ADT_YR = RELORG.ADT_YR(+) AND MASTER.ADT_NO = RELORG.ADT_NO(+) AND MASTER.DSPS_RQT_SQNO = RELORG.DSPS_RQT_SQNO(+) AND MASTER.DSPS_RQT_KDCD = '110' AND RELORG.ADT_YR = RELPSN.ADT_YR(+) AND RELORG.ADT_NO = RELPSN.ADT_NO(+) AND RELORG.DSPS_RQT_SQNO = RELPSN.DSPS_RQT_SQNO(+) AND RELORG.REL_INST_CD = RELPSN.REL_INST_CD(+) AND MASTER.CVLCPT_ADT_YR = T1.ADT_YR AND MASTER.ADT_NO = T1.ADT_NO AND DETAIL.ENFC_YMD >= #strEnfDtSt# AND DETAIL.ENFC_YMD AND MASTER.CVLCPT_ADT_YR >= #strAudYrSt# AND T1.ADT_GRNT_NO >= #strAudGrnNoSt# AND MASTER.CVLCPT_ADT_YR AND T1.ADT_GRNT_NO /* 감사연번종류코드 추가 - 2015.12.24 yyh */ AND SUBSTR(T1.ADT_KDCD,3,1) = NVL(#strAudYrNoKndCdSt#,'0') AND DETAIL.ENFC_DEPT_CD = #ENFC_DEPT_CD# AND MASTER.DSPS_RQT_NM LIKE '%'||#strTitTxt#||'%' AND RELORG.REL_INST_CD = #SRECS_REL_INST_CD# AND RELPSN.RLPR_NM LIKE #strDfntNam#||'%' ORDER BY MASTER.CVLCPT_ADT_YR DESC ,MASTER.ADT_NO DESC ,MASTER.DSPS_RQT_SQNO DESC