SELECT D1.ADT_YR AS ADT_YR /* 감사년도 */,
    D1.ADT_NO AS ADT_NO /* 감사번호 */,
    D1.DSPS_RQT_SQNO AS DSPS_RQT_SQNO /* 처분요구순번 */,
    F_MNG_KND_AUDYRNO(D1.ADT_YR, D1.ADT_NO, '', '-') ||'-'|| D1.DSPS_RQT_SQNO AS F_MNG_KND_AUDYRNO,
    (SELECT CASE WHEN NTFCTN_RCPT_SQNO IS NOT NULL THEN LINK_YR || '-' || DECODE(OCRN_SECD,'6','판청','손망') || '-' || NTFCTN_RCPT_SQNO ELSE '' AS NTFCTN_RCPT_SQNO /* 통보접수순번 */
FROM TB_BADGDA001M WHERE INST_CD = D1.DSPS_RQT_NM_1 AND NTFCTN_SECD = D1.DSPS_RQT_NM_2 AND NTFCTN_SQNO = D1.DSPS_RQT_NM_3) AS ACP_NO_TXT FROM TB_BADEAR035L D1 WHERE 1=1 AND D1.CVLCPT_ADT_YR = #CVLCPT_ADT_YR# AND D1.ADT_NO = #ADT_NO# AND D1.DSPS_RQT_SQNO = #DSPS_RQT_SQNO# AND D1.USER_HSTRY_SECD = '8'