SELECT SUBSTR(T1.LINK_TRRC_DT,1,8) AS PROF_RBPRSN_LINK_DT /* 증명책임자연계일시 */,
    CASE WHEN T1.TASK_SECD = '01' THEN '처분요구사항' WHEN T1.TASK_SECD = '02' THEN '관계자' WHEN T1.TASK_SECD = '03' THEN '감사자' WHEN T1.TASK_SECD = '04' THEN '처분요구사항상세' WHEN T1.TASK_SECD = '05' THEN '관계기관' WHEN T1.TASK_SECD = '06' THEN '회계사항' WHEN T1.TASK_SECD = '07' THEN '소관기관' WHEN T1.TASK_SECD = '08' THEN '감사사항' WHEN T1.TASK_SECD = '09' THEN '국과별감사처리' WHEN T1.TASK_SECD = '10' THEN '처분요구요약전' ELSE T1.TASK_SECD END AS TASK_SECD /* 업무구분코드 */,
    T1.LINK_PRCS_STTS_SECD /* 연계처리상태구분코드 */,
    COUNT(1) AS 1
FROM IS_REQINFO T1 WHERE SUBSTR(T1.LINK_TRRC_DT,1,8) BETWEEN #strConDtStr# AND #strConDtEnd# AND T1.TASK_SECD = #TASK_SECD# GROUP BY SUBSTR(T1.LINK_TRRC_DT,1,8) ,T1.TASK_SECD ,T1.LINK_PRCS_STTS_SECD ORDER BY SUBSTR(T1.LINK_TRRC_DT,1,8) DESC ,T1.TASK_SECD ,T1.LINK_PRCS_STTS_SECD