SELECT SUBSTR(T1.LINK_TRRC_DT,1,8) AS PROF_RBPRSN_LINK_DT /* 증명책임자연계일시 */,
    CASE WHEN T1.TASK_SECD = '01' THEN '집행전말기본' WHEN T1.TASK_SECD = '02' THEN '집행전말이력' WHEN T1.TASK_SECD = '03' THEN '집행단계별계획' WHEN T1.TASK_SECD = '04' THEN '집행전말관계기관기본변경' WHEN T1.TASK_SECD = '05' THEN '집행전말관계자기본변경' WHEN T1.TASK_SECD = '06' THEN '특수관리기본' WHEN T1.TASK_SECD = '07' THEN '특수관리집행내역' WHEN T1.TASK_SECD = '08' THEN '' WHEN T1.TASK_SECD = '09' THEN '통보사항기본삭제내역' WHEN T1.TASK_SECD = '10' THEN '' WHEN T1.TASK_SECD = '11' THEN '집행전말기본삭제이력(삭제전문)' WHEN T1.TASK_SECD = '12' THEN '집행전말기본삭제이력' WHEN T1.TASK_SECD = '13' THEN '집행단계별계획삭제이력' ELSE T1.TASK_SECD END AS TASK_SECD /* 업무구분코드 */,
    T1.LINK_PRCS_STTS_SECD /* 연계처리상태구분코드 */,
    COUNT(1) AS 1
FROM IS_UPDATEINFO T1 WHERE SUBSTR(T1.LINK_TRRC_DT,1,8) BETWEEN #strConDtStr# AND #strConDtEnd# AND T1.TASK_SECD = #TASK_SECD# GROUP BY SUBSTR(T1.LINK_TRRC_DT,1,8) ,T1.TASK_SECD ,T1.LINK_PRCS_STTS_SECD ORDER BY SUBSTR(T1.LINK_TRRC_DT,1,8) DESC ,T1.TASK_SECD ,T1.LINK_PRCS_STTS_SECD