SELECT SUBSTR(T1.LINK_RCPTN_DT,1,8) AS PROF_RBPRSN_LINK_DT /* 증명책임자연계일시 */,
    CASE WHEN T1.TASK_SECD = '01' THEN '기관코드신청' WHEN T1.TASK_SECD = '02' THEN '기관코드직접입력' ELSE T1.TASK_SECD END AS TASK_SECD /* 업무구분코드 */,
    T1.LINK_PRCS_STTS_TPCD /* 연계처리상태유형코드 */,
    COUNT(1) AS 1
FROM IR_ORG_USERETC T1 WHERE SUBSTR(T1.LINK_RCPTN_DT,1,8) BETWEEN #strConDtStr# AND #strConDtEnd# AND T1.TASK_SECD = #TASK_SECD# GROUP BY SUBSTR(T1.LINK_RCPTN_DT,1,8) ,T1.TASK_SECD ,T1.LINK_PRCS_STTS_TPCD ORDER BY SUBSTR(T1.LINK_RCPTN_DT,1,8) DESC ,T1.TASK_SECD ,T1.LINK_PRCS_STTS_TPCD