SELECT SUBSTR(T1.LINK_TRRC_DT,1,8) AS PROF_RBPRSN_LINK_DT /* 증명책임자연계일시 */,
    CASE WHEN T1.STT_NTFCTN_SECD = '100' THEN '계약체결사항' WHEN T1.STT_NTFCTN_SECD = '200' THEN '국유재산교환' ELSE T1.STT_NTFCTN_SECD END AS STT_NTFCTN_SECD /* 법령통보구분코드 */,
    T1.LINK_PRCS_STTS_SECD /* 연계처리상태구분코드 */,
    COUNT(1) AS 1
FROM IR_REL_LAW_NTF T1 WHERE SUBSTR(T1.LINK_TRRC_DT,1,8) BETWEEN #strConDtStr# AND #strConDtEnd# AND T1.STT_NTFCTN_SECD = #STT_NTFCTN_SECD# GROUP BY SUBSTR(T1.LINK_TRRC_DT,1,8) ,T1.STT_NTFCTN_SECD ,T1.LINK_PRCS_STTS_SECD ORDER BY SUBSTR(T1.LINK_TRRC_DT,1,8) DESC ,T1.STT_NTFCTN_SECD ,T1.LINK_PRCS_STTS_SECD