SELECT SUBSTR(T1.LINK_TRRC_DT,1,8) AS PROF_RBPRSN_LINK_DT /* 증명책임자연계일시 */,
    CASE WHEN T1.LINK_MENU_ID = 'aa.ina.AdActMng' THEN '처분요구' WHEN T1.LINK_MENU_ID = 'ni.crn.CrimNoti' THEN '범죄' WHEN T1.LINK_MENU_ID = 'ni.dcn.DiscipNt' THEN '징계' WHEN T1.LINK_MENU_ID = 'ni.ldn.LosDmgNt' THEN '손망실' ELSE T1.LINK_MENU_ID END AS LINK_MENU_ID /* 연계메뉴아이디 */,
    T1.LINK_PRCS_STTS_SECD /* 연계처리상태구분코드 */,
    COUNT(1) AS 1
FROM IR_JUMINNOINFO T1 WHERE SUBSTR(T1.LINK_TRRC_DT,1,8) BETWEEN #strConDtStr# AND #strConDtEnd# AND T1.LINK_MENU_ID = #LINK_MENU_ID# GROUP BY SUBSTR(T1.LINK_TRRC_DT,1,8) ,T1.LINK_MENU_ID ,T1.LINK_PRCS_STTS_SECD ORDER BY SUBSTR(T1.LINK_TRRC_DT,1,8) DESC ,T1.LINK_MENU_ID ,T1.LINK_PRCS_STTS_SECD